rvice\nfrom io.jans.as.service.net import HttpService\nfrom io.jans.as.util import ServerUtil\nfrom io.jans.util import StringHelper\nfrom io.jans.as.service.common import EncryptionService, UserService\nfrom io.jans.service import MailService\nfrom io.jans.as.service.push.sns import PushPlatform, PushSnsService \nfrom io.jans.oxnotify.client import NotifyClientFactory \nfrom java.util import Arrays, HashMap, IdentityHashMap, Date\nfrom java.time import ZonedDateTime\nfrom java.time.format import DateTimeFormatter\n\ntry:\n    from io.jans.oxd.license.client.js import Product\n    from io.jans.oxd.license.validator import LicenseValidator\n    has_license_api = True\nexcept ImportError:\n    print \"Super-Janssen. Load. Failed to load licensing API\"\n    has_license_api = False\n\nimport datetime\nimport urllib\n\nimport sys\nimport json\n\nclass PersonAuthentication(PersonAuthenticationType):\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript, configurationAttributes):\n        print \"Super-Janssen. Initialization\"\n\n        if not configurationAttributes.containsKey(\"authentication_mode\"):\n            print \"Super-Janssen. Initialization. Property authentication_mode is mandatory\"\n            return False\n\n        self.applicationId = None\n        if configurationAttributes.containsKey(\"application_id\"):\n            self.applicationId = configurationAttributes.get(\"application_id\").getValue2()\n\n        self.registrationUri = None\n        if configurationAttributes.containsKey(\"registration_uri\"):\n            self.registrationUri = configurationAttributes.get(\"registration_uri\").getValue2()\n\n        authentication_mode = configurationAttributes.get(\"authentication_mode\").getValue2()\n        if StringHelper.isEmpty(authentication_mode):\n            print \"Super-Janssen. Initialization. Failed to determine authentication_mode. authentication_mode configuration parameter is empty\"\n            return False\n        \n        self.oneStep = StringHelper.equalsIgnoreCase(authentication_mode, \"one_step\")\n        self.twoStep = StringHelper.equalsIgnoreCase(authentication_mode, \"two_step\")\n\n        if not (self.oneStep or self.twoStep):\n            print \"Super-Janssen. Initialization. Valid authentication_mode values are one_step and two_step\"\n            return False\n        \n        self.enabledPushNotifications = self.initPushNotificationService(configurationAttributes)\n\n        self.androidUrl = None\n        if configurationAttributes.containsKey(\"supe.jans.android_download_url\"):\n            self.androidUrl = configurationAttributes.get(\"supe.jans.android_download_url\").getValue2()\n\n        self.IOSUrl = None\n        if configurationAttributes.containsKey(\"supe.jans.ios_download_url\"):\n            self.IOSUrl = configurationAttributes.get(\"supe.jans.ios_download_url\").getValue2()\n\n        self.customLabel = None\n        if configurationAttributes.containsKey(\"label\"):\n            self.customLabel = configurationAttributes.get(\"label\").getValue2()\n\n        self.customQrOptions = {}\n        if configurationAttributes.containsKey(\"qr_options\"):\n            self.customQrOptions = configurationAttributes.get(\"qr_options\").getValue2()\n\n        self.use_super.jans.group = False\n        if configurationAttributes.containsKey(\"super.jans.group\"):\n            self.super.jans.group = configurationAttributes.get(\"super.jans.group\").getValue2()\n            self.use_super.jans.group = True\n            print \"Super-Janssen. Initialization. Using super.jans.only if user belong to group: %s\" % self.super.jans.group\n\n        self.use_audit_group = False\n        if configurationAttributes.containsKey(\"audit_group\"):\n            self.audit_group = configurationAttributes.get(\"audit_group\").getValue2()\n\n            if (not configurationAttributes.containsKey(\"audit_group_email\")):\n                print \"Super-Janssen. Initialization. Property audit_group_email is not specified\"\n                return False\n\n            self.audit_email = configurationAttributes.get(\"audit_group_email\").getValue2()\n            self.use_audit_group = True\n\n            print \"Super-Janssen. Initialization. Using audit group: %s\" % self.audit_group\n            \n        if self.use_super.jans.group or self.use_audit_group:\n            if not configurationAttributes.containsKey(\"audit_attribute\"):\n                print \"Super-Janssen. Initialization. Property audit_attribute is not specified\"\n                return False\n            else:\n                self.audit_attribute = configurationAttributes.get(\"audit_attribute\").getValue2()\n\n        self.valid_license = False\n        # Removing or altering this block validation is against the terms of the license. \n        if has_license_api and configurationAttributes.containsKey(\"license_file\"):\n            license_file = configurationAttributes.get(\"license_file\").getValue2()\n\n            # Load license from file\n            f = open(license_file, 'r')\n            try:\n                license = json.loads(f.read())\n            except:\n                print \"Super-Janssen. Initialization. Failed to load license from file: %s\" % license_file\n                return False\n            finally:\n                f.close()\n            \n            # Validate license\n            try:\n                self.license_content = LicenseValidator.validate(license[\"public_key\"], license[\"public_password\"], license[\"license_password\"], license[\"license\"],\n                                          Product.SUPER_JANS, Date())\n                self.valid_license = self.license_content.isValid()\n            except:\n                print \"Super-Janssen. Initialization. Failed to validate license. Exception: \", sys.exc_info()[1]\n                return False\n\n            print \"Super-Janssen. Initialization. License status: '%s'. License metadata: '%s'\" % (self.valid_license, self.license_content.getMetadata())\n\n        print \"Super-Janssen. Initialized successfully. oneStep: '%s', twoStep: '%s', pushNotifications: '%s', customLabel: '%s'\" % (self.oneStep, self.twoStep, self.enabledPushNotifications, self.customLabel)\n\n        return True   \n\n    def destroy(self, configurationAttributes):\n        print \"Super-Janssen. Destroy\"\n\n        self.pushAndroidService = None\n        self.pushAppleService = None\n\n        print \"Super-Janssen. Destroyed successfully\"\n        return True\n\n    def getApiVersion(self):\n        return 11\n        \n    def getAuthenticationMethodClaims(self, requestParameters):\n        return None\n        \n    def isValidAuthenticationMethod(self, usageType, configurationAttributes):\n        return True\n\n    def getAlternativeAuthenticationMethod(self, usageType, configurationAttributes):\n        return None\n\n    def authenticate(self, configurationAttributes, requestParameters, step):\n        authenticationService = CdiUtil.bean(AuthenticationService)\n\n        identity = CdiUtil.bean(Identity)\n        credentials = identity.getCredentials()\n\n        session_attributes = identity.getSessionId().getSessionAttributes()\n\n        client_redirect_uri = self.getApplicationUri(session_attributes)\n        if client_redirect_uri == None:\n            print \"Super-Janssen. Authenticate. redirect_uri is not set\"\n            return False\n\n        self.setRequestScopedParameters(identity, step)\n\n        # Validate form result code and initialize QR code regeneration if needed (retry_current_step = True)\n        identity.setWorkingParameter(\"retry_current_step\", False)\n        form_auth_result = ServerUtil.getFirstValue(requestParameters, \"auth_result\")\n        if StringHelper.isNotEmpty(form_auth_result):\n            print \"Super-Janssen. Authenticate for step %s. Get auth_result: '%s'\" % (step, form_auth_result)\n            if form_auth_result in ['error']:\n                return False\n\n            if form_auth_result in ['timeout']:\n                if ((step == 1) and self.oneStep) or ((step == 2) and self.twoStep):        \n                    print \"Super-Janssen. Authenticate for step %s. Reinitializing current step\" % step\n                    identity.setWorkingParameter(\"retry_current_step\", True)\n                    return False\n\n        userService = CdiUtil.bean(UserService)\n        deviceRegistrationService = CdiUtil.bean(DeviceRegistrationService)\n        if step == 1:\n            print \"Super-Janssen. Authenticate for step 1\"\n\n            user_name = credentials.getUsername()\n            if self.oneStep:\n                session_device_status = self.getSessionDeviceStatus(session_attributes, user_name)\n                if session_device_status == None:\n                    return False\n\n                u2f_device_id = session_device_status['device_id']\n\n                validation_result = self.validateSessionDeviceStatus(client_redirect_uri, session_device_status)\n                if validation_result:\n                    print \"Super-Janssen. Authenticate for step 1. User successfully authenticated with u2f_device '%s'\" % u2f_device_id\n                else:\n                    return False\n                    \n                if not session_device_status['one_step']:\n                    print \"Super-Janssen. Authenticate for step 1. u2f_device '%s' is not one step device\" % u2f_device_id\n                    return False\n                    \n                # There are two steps only in enrollment mode\n                if session_device_status['enroll']:\n                    return validation_result\n\n                identity.setWorkingParameter(\"super.jans.count_login_steps\", 1)\n\n                user_inum = session_device_status['user_inum']\n\n                u2f_device = deviceRegistrationService.findUserDeviceRegistration(user_inum, u2f_device_id, \"oxId\")\n                if u2f_device == None:\n                    print \"Super-Janssen. Authenticate for step 1. Failed to load u2f_device '%s'\" % u2f_device_id\n                    return False\n\n                logged_in = authenticationService.authenticate(user_name)\n                if not logged_in:\n                    print \"Super-Janssen. Authenticate for step 1. Failed to authenticate user '%s'\" % user_name\n                    return False\n\n                print \"Super-Janssen. Authenticate for step 1. User '%s' successfully authenticated with u2f_device '%s'\" % (user_name, u2f_device_id)\n                \n                return True\n            elif self.twoStep:\n                authenticated_user = self.processBasicAuthentication(credentials)\n                if authenticated_user == None:\n                    return False\n\n                if (self.use_super.jans.group):\n                    print \"Super-Janssen. Authenticate for step 1. Checking if user belong to super.jans.group\"\n                    is_member_super.jans.group = self.isUserMemberOfGroup(authenticated_user, self.audit_attribute, self.super.jans.group)\n                    if (is_member_super.jans.group):\n                        print \"Super-Janssen. Authenticate for step 1. User '%s' member of super.jans.group\" % authenticated_user.getUserId()\n                        super.jans.count_login_steps = 2\n                    else:\n                        if self.use_audit_group:\n                            self.processAuditGroup(authenticated_user, self.audit_attribute, self.audit_group)\n                        super.jans.count_login_steps = 1\n    \n                    identity.setWorkingParameter(\"super.jans.count_login_steps\", super.jans.count_login_steps)\n                    \n                    if super.jans.count_login_steps == 1:\n                        return True\n    \n                auth_method = 'authenticate'\n                enrollment_mode = ServerUtil.getFirstValue(requestParameters, \"loginForm:registerButton\")\n                if StringHelper.isNotEmpty(enrollment_mode):\n                    auth_method = 'enroll'\n                \n                if auth_method == 'authenticate':\n                    user_inum = userService.getUserInum(authenticated_user)\n                    u2f_devices_list = deviceRegistrationService.findUserDeviceRegistrations(user_inum, client_redirect_uri, \"oxId\")\n                    if u2f_devices_list.size() == 0:\n                        auth_method = 'enroll'\n                        print \"Super-Janssen. Authenticate for step 1. There is no U2F '%s' user devices associated with application '%s'. Changing auth_method to '%s'\" % (user_name, client_redirect_uri, auth_method)\n    \n                print \"Super-Janssen. Authenticate for step 1. auth_method: '%s'\" % auth_method\n                \n                identity.setWorkingParameter(\"super.jans.auth_method\", auth_method)\n\n                return True\n\n            return False\n        elif step == 2:\n            print \"Super-Janssen. Authenticate for step 2\"\n\n            user = authenticationService.getAuthenticatedUser()\n            if (user == None):\n                print \"Super-Janssen. Authenticate for step 2. Failed to determine user name\"\n                return False\n            user_name = user.getUserId()\n\n            session_attributes = identity.getSessionId().getSessionAttributes()\n\n            session_device_status = self.getSessionDeviceStatus(session_attributes, user_name)\n            if session_device_status == None:\n                return False\n\n            u2f_device_id = session_device_status['device_id']\n\n            # There are two steps only in enrollment mode\n            if self.oneStep and session_device_status['enroll']:\n                authenticated_user = self.processBasicAuthentication(credentials)\n                if authenticated_user == None:\n                    return False\n\n                user_inum = userService.getUserInum(authenticated_user)\n                \n                attach_result = deviceRegistrationService.attachUserDeviceRegistration(user_inum, u2f_device_id)\n\n                print \"Super-Janssen. Authenticate for step 2. Result after attaching u2f_device '%s' to user '%s': '%s'\" % (u2f_device_id, user_name, attach_result) \n\n                return attach_result\n            elif self.twoStep:\n                if user_name == None:\n                    print \"Super-Janssen. Authenticate for step 2. Failed to determine user name\"\n                    return False\n\n                validation_result = self.validateSessionDeviceStatus(client_redirect_uri, session_device_status, user_name)\n                if validation_result:\n                    print \"Super-Janssen. Authenticate for step 2. User '%s' successfully authenticated with u2f_device '%s'\" % (user_name, u2f_device_id)\n                else:\n                    return False\n                \n                super.jans.request = json.loads(session_device_status['super.jans.request'])\n                auth_method = super.jans.request['method']\n                if auth_method in ['enroll', 'authenticate']:\n                    if validation_result and self.use_audit_group:\n                        user = authenticationService.getAuthenticatedUser()\n                        self.processAuditGroup(user, self.audit_attribute, self.audit_group)\n\n                    return validation_result\n\n                print \"Super-Janssen. Authenticate for step 2. U2F auth_method is invalid\"\n\n            return False\n        else:\n            return False\n\n    def prepareForStep(self, configurationAttributes, requestParameters, step):\n        identity = CdiUtil.bean(Identity)\n        session_attributes = identity.getSessionId().getSessionAttributes()\n\n        client_redirect_uri = self.getApplicationUri(session_attributes)\n        if client_redirect_uri == None:\n            print \"Super-Janssen. Prepare for step. redirect_uri is not set\"\n            return False\n\n        self.setRequestScopedParameters(identity, step)\n\n        if step == 1:\n            print \"Super-Janssen. Prepare for step 1\"\n            if self.oneStep:\n                session = CdiUtil.bean(SessionIdService).getSessionId()\n                if session == None:\n                    print \"Super-Janssen. Prepare for step 2. Failed to determine session_id\"\n                    return False\n\n                issuer = CdiUtil.bean(ConfigurationFactory).getConfiguration().getIssuer()\n                super.jans.request_dictionary = {'app': client_redirect_uri,\n                                   'issuer': issuer,\n                                   'state': session.getId(),\n                                   'licensed': self.valid_license,\n                                   'created': DateTimeFormatter.ISO_OFFSET_DATE_TIME.format(ZonedDateTime.now().withNano(0))}\n\n                self.addGeolocationData(session_attributes, super.jans.request_dictionary)\n\n                super.jans.request = json.dumps(super.jans.request_dictionary, separators=(',',':'))\n                print \"Super-Janssen. Prepare for step 1. Prepared super.jans.request:\", super.jans.request\n    \n                identity.setWorkingParameter(\"super.jans.request\", super.jans.request)\n            elif self.twoStep:\n                identity.setWorkingParameter(\"display_register_action\", True)\n\n            return True\n        elif step == 2:\n            print \"Super-Janssen. Prepare for step 2\"\n            if self.oneStep:\n                return True\n\n            authenticationService = CdiUtil.bean(AuthenticationService)\n            user = authenticationService.getAuthenticatedUser()\n            if user == None:\n                print \"Super-Janssen. Prepare for step 2. Failed to determine user name\"\n                return False\n\n            if session_attributes.containsKey(\"super.jans.request\"):\n               super.jans.request = session_attributes.get(\"super.jans.request\")\n               if not StringHelper.equalsIgnoreCase(super.jans.request, \"timeout\"):\n                   print \"Super-Janssen. Prepare for step 2. Request was generated already\"\n                   return True\n            \n            session = CdiUtil.bean(SessionIdService).getSessionId()\n            if session == None:\n                print \"Super-Janssen. Prepare for step 2. Failed to determine session_id\"\n                return False\n\n            auth_method = session_attributes.get(\"super.jans.auth_method\")\n            if StringHelper.isEmpty(auth_method):\n                print \"Super-Janssen. Prepare for step 2. Failed to determine auth_method\"\n                return False\n\n            print \"Super-Janssen. Prepare for step 2. auth_method: '%s'\" % auth_method\n            \n            issuer = CdiUtil.bean(ConfigurationFactory).getAppConfiguration().getIssuer()\n            super.jans.request_dictionary = {'username': user.getUserId(),\n                               'app': client_redirect_uri,\n                               'issuer': issuer,\n                               'method': auth_method,\n                               'state': session.getId(),\n                               'licensed': self.valid_license,\n                               'created': DateTimeFormatter.ISO_OFFSET_DATE_TIME.format(ZonedDateTime.now().withNano(0))}\n\n            self.addGeolocationData(session_attributes, super.jans.request_dictionary)\n\n            super.jans.request = json.dumps(super.jans.request_dictionary, separators=(',',':'))\n            print \"Super-Janssen. Prepare for step 2. Prepared super.jans.request:\", super.jans.request\n\n            identity.setWorkingParameter(\"super.jans.request\", super.jans.request)\n            identity.setWorkingParameter(\"super.jans.auth_method\", auth_method)\n\n            if auth_method in ['authenticate']:\n                self.sendPushNotification(client_redirect_uri, user, super.jans.request)\n\n            return True\n        else:\n            return False\n\n    def getNextStep(self, configurationAttributes, requestParameters, step):\n        # If user not pass current step change step to previous\n        identity = CdiUtil.bean(Identity)\n        retry_current_step = identity.getWorkingParameter(\"retry_current_step\")\n        if retry_current_step:\n            print \"Super-Janssen. Get next step. Retrying current step\"\n\n            # Remove old QR code\n            identity.setWorkingParameter(\"super.jans.request\", \"timeout\")\n\n            resultStep = step\n            return resultStep\n\n        return -1\n\n    def getExtraParametersForStep(self, configurationAttributes, step):\n        if step == 1:\n            if self.oneStep:        \n                return Arrays.asList(\"super.jans.request\")\n            elif self.twoStep:\n                return Arrays.asList(\"display_register_action\")\n        elif step == 2:\n            return Arrays.asList(\"super.jans.auth_method\", \"super.jans.request\")\n        \n        return None\n\n    def getCountAuthenticationSteps(self, configurationAttributes):\n        identity = CdiUtil.bean(Identity)\n        if identity.isSetWorkingParameter(\"super.jans.count_login_steps\"):\n            return identity.getWorkingParameter(\"super.jans.count_login_steps\")\n        else:\n            return 2\n\n    def getPageForStep(self, configurationAttributes, step):\n        if step == 1:\n            if self.oneStep:        \n                return \"/auth/super.jans.login.xhtml\"\n        elif step == 2:\n            if self.oneStep:\n                return \"/login.xhtml\"\n            else:\n                identity = CdiUtil.bean(Identity)\n                authmethod = identity.getWorkingParameter(\"super.jans.auth_method\")\n                print \"Super-Janssen. authmethod '%s'\" % authmethod\n                if authmethod == \"enroll\":\n                    return \"/auth/super.jans.login.xhtml\"\n                else:\n                    return \"/auth/super.jans.login.xhtml\"\n\n        return \"\"\n\n    def getLogoutExternalUrl(self, configurationAttributes, requestParameters):\n        print \"Get external logout URL call\"\n        return None\n\n    def logout(self, configurationAttributes, requestParameters):\n        return True\n\n    def processBasicAuthentication(self, credentials):\n        authenticationService = CdiUtil.bean(AuthenticationService)\n\n        user_name = credentials.getUsername()\n        user_password = credentials.getPassword()\n\n        logged_in = False\n        if StringHelper.isNotEmptyString(user_name) and StringHelper.isNotEmptyString(user_password):\n            logged_in = authenticationService.authenticate(user_name, user_password)\n\n        if not logged_in:\n            return None\n\n        find_user_by_uid = authenticationService.getAuthenticatedUser()\n        if find_user_by_uid == None:\n            print \"Super-Janssen. Process basic authentication. Failed to find user '%s'\" % user_name\n            return None\n        \n        return find_user_by_uid\n\n    def validateSessionDeviceStatus(self, client_redirect_uri, session_device_status, user_name = None):\n        userService = CdiUtil.bean(UserService)\n        deviceRegistrationService = CdiUtil.bean(DeviceRegistrationService)\n\n        u2f_device_id = session_device_status['device_id']\n\n        u2f_device = None\n        if session_device_status['enroll'] and session_device_status['one_step']:\n            u2f_device = deviceRegistrationService.findOneStepUserDeviceRegistration(u2f_device_id)\n            if u2f_device == None:\n                print \"Super-Janssen. Validate session device status. There is no one step u2f_device '%s'\" % u2f_device_id\n                return False\n        else:\n            # Validate if user has specified device_id enrollment\n            user_inum = userService.getUserInum(user_name)\n\n            if session_device_status['one_step']:\n                user_inum = session_device_status['user_inum']\n    \n            u2f_device = deviceRegistrationService.findUserDeviceRegistration(user_inum, u2f_device_id)\n            if u2f_device == None:\n                print \"Super-Janssen. Validate session device status. There is no u2f_device '%s' associated with user '%s'\" % (u2f_device_id, user_inum)\n                return False\n\n        if not StringHelper.equalsIgnoreCase(client_redirect_uri, u2f_device.application):\n            print \"Super-Janssen. Validate session device status. u2f_device '%s' associated with other application '%s'\" % (u2f_device_id, u2f_device.application)\n            return False\n        \n        return True\n\n    def getSessionDeviceStatus(self, session_attributes, user_name):\n        print \"Super-Janssen. Get session device status\"\n\n        if not session_attributes.containsKey(\"super.jans.request\"):\n            print \"Super-Janssen. Get session device status. There is no Super-Janssen request in session attributes\"\n            return None\n\n        # Check session state extended\n        if not session_attributes.containsKey(\"session_custom_state\"):\n            print \"Super-Janssen. Get session device status. There is no session_custom_state in session attributes\"\n            return None\n\n        session_custom_state = session_attributes.get(\"session_custom_state\")\n        if not StringHelper.equalsIgnoreCase(\"approved\", session_custom_state):\n            print \"Super-Janssen. Get session device status. User '%s' not approve or not pass U2F authentication. session_custom_state: '%s'\" % (user_name, session_custom_state)\n            return None\n\n        # Try to find device_id in session attribute\n        if not session_attributes.containsKey(\"oxpush2_u2f_device_id\"):\n            print \"Super-Janssen. Get session device status. There is no u2f_device associated with this request\"\n            return None\n\n        # Try to find user_inum in session attribute\n        if not session_attributes.containsKey(\"oxpush2_u2f_device_user_inum\"):\n            print \"Super-Janssen. Get session device status. There is no user_inum associated with this request\"\n            return None\n        \n        enroll = False\n        if session_attributes.containsKey(\"oxpush2_u2f_device_enroll\"):\n            enroll = StringHelper.equalsIgnoreCase(\"true\", session_attributes.get(\"oxpush2_u2f_device_enroll\"))\n\n        one_step = False\n        if session_attributes.containsKey(\"oxpush2_u2f_device_one_step\"):\n            one_step = StringHelper.equalsIgnoreCase(\"true\", session_attributes.get(\"oxpush2_u2f_device_one_step\"))\n                        \n        super.jans.request = session_attributes.get(\"super.jans.request\")\n        u2f_device_id = session_attributes.get(\"oxpush2_u2f_device_id\")\n        user_inum = session_attributes.get(\"oxpush2_u2f_device_user_inum\")\n\n        session_device_status = {\"super.jans.request\": super.jans.request, \"device_id\": u2f_device_id, \"user_inum\" : user_inum, \"enroll\" : enroll, \"one_step\" : one_step}\n        print \"Super-Janssen. Get session device status. session_device_status: '%s'\" % (session_device_status)\n        \n        return session_device_status\n\n    def initPushNotificationService(self, configurationAttributes):\n        print \"Super-Janssen. Initialize Native/SNS/Janssen notification services\"\n\n        self.pushSnsMode = False\n        self.pushJanssenMode = False\n        if configurationAttributes.containsKey(\"notification_service_mode\"):\n            notificationServiceMode = configurationAttributes.get(\"notification_service_mode\").getValue2()\n            if StringHelper.equalsIgnoreCase(notificationServiceMode, \"sns\"):\n                return self.initSnsPushNotificationService(configurationAttributes)\n            elif StringHelper.equalsIgnoreCase(notificationServiceMode, .jans.):\n                return self.initJanssenPushNotificationService(configurationAttributes)\n\n        return self.initNativePushNotificationService(configurationAttributes)\n\n    def initNativePushNotificationService(self, configurationAttributes):\n        print \"Super-Janssen. Initialize native notification services\"\n        \n        creds = self.loadPushNotificationCreds(configurationAttributes)\n        if creds == None:\n            return False\n        \n        try:\n            android_creds = creds[\"android\"][\"gcm\"]\n            ios_creds = creds[\"ios\"][\"apns\"]\n        except:\n            print \"Super-Janssen. Initialize native notification services. Invalid credentials file format\"\n            return False\n        \n        self.pushAndroidService = None\n        self.pushAppleService = None\n        if android_creds[\"enabled\"]:\n            self.pushAndroidService = Sender(android_creds[\"api_key\"]) \n            print \"Super-Janssen. Initialize native notification services. Created Android notification service\"\n            \n        if ios_creds[\"enabled\"]:\n            p12_file_path = ios_creds[\"p12_file_path\"]\n            p12_password = ios_creds[\"p12_password\"]\n\n            try:\n                encryptionService = CdiUtil.bean(EncryptionService)\n                p12_password = encryptionService.decrypt(p12_password)\n            except:\n                # Ignore exception. Password is not encrypted\n                print \"Super-Janssen. Initialize native notification services. Assuming that 'p12_password' password in not encrypted\"\n\n            apnsServiceBuilder =  APNS.newService().withCert(p12_file_path, p12_password)\n            if ios_creds[\"production\"]:\n                self.pushAppleService = apnsServiceBuilder.withProductionDestination().build()\n            else:\n                self.pushAppleService = apnsServiceBuilder.withSandboxDestination().build()\n\n            self.pushAppleServiceProduction = ios_creds[\"production\"]\n\n            print \"Super-Janssen. Initialize native notification services. Created iOS notification service\"\n\n        enabled = self.pushAndroidService != None or self.pushAppleService != None\n\n        return enabled\n\n    def initSnsPushNotificationService(self, configurationAttributes):\n        print \"Super-Janssen. Initialize SNS notification services\"\n        self.pushSnsMode = True\n\n        creds = self.loadPushNotificationCreds(configurationAttributes)\n        if creds == None:\n            return False\n        \n        try:\n            sns_creds = creds[\"sns\"]\n            android_creds = creds[\"android\"][\"sns\"]\n            ios_creds = creds[\"ios\"][\"sns\"]\n        except:\n            print \"Super-Janssen. Initialize SNS notification services. Invalid credentials file format\"\n            return False\n        \n        self.pushAndroidService = None\n        self.pushAppleService = None\n        if not (android_creds[\"enabled\"] or ios_creds[\"enabled\"]):\n            print \"Super-Janssen. Initialize SNS notification services. SNS disabled for all platforms\"\n            return False\n\n        sns_access_key = sns_creds[\"access_key\"]\n        sns_secret_access_key = sns_creds[\"secret_access_key\"]\n        sns_region = sns_creds[\"region\"]\n\n        encryptionService = CdiUtil.bean(EncryptionService)\n\n        try:\n            sns_secret_access_key = encryptionService.decrypt(sns_secret_access_key)\n        except:\n            # Ignore exception. Password is not encrypted\n            print \"Super-Janssen. Initialize SNS notification services. Assuming that 'sns_secret_access_key' in not encrypted\"\n        \n        pushSnsService = CdiUtil.bean(PushSnsService)\n        pushClient = pushSnsService.createSnsClient(sns_access_key, sns_secret_access_key, sns_region)\n\n        if android_creds[\"enabled\"]:\n            self.pushAndroidService = pushClient\n            self.pushAndroidPlatformArn = android_creds[\"platform_arn\"]\n            print \"Super-Janssen. Initialize SNS notification services. Created Android notification service\"\n\n        if ios_creds[\"enabled\"]:\n            self.pushAppleService = pushClient \n            self.pushApplePlatformArn = ios_creds[\"platform_arn\"]\n            self.pushAppleServiceProduction = ios_creds[\"production\"]\n            print \"Super-Janssen. Initialize SNS notification services. Created iOS notification service\"\n\n        enabled = self.pushAndroidService != None or self.pushAppleService != None\n\n        return enabled\n\n    def initJanssenPushNotificationService(self, configurationAttributes):\n        print \"Super-Janssen. Initialize Janssen notification services\"\n\n        self.pushJanssenMode = True\n\n        creds = self.loadPushNotificationCreds(configurationAttributes)\n        if creds == None:\n            return False\n        \n        try:\n           .jans.conf = creds[.jans.]\n            android_creds = creds[\"android\"][.jans.]\n            ios_creds = creds[\"ios\"][.jans.]\n        except:\n            print \"Super-Janssen. Initialize Janssen notification services. Invalid credentials file format\"\n            return False\n        \n        self.pushAndroidService = None\n        self.pushAppleService = None\n        if not (android_creds[\"enabled\"] or ios_creds[\"enabled\"]):\n            print \"Super-Janssen. Initialize Janssen notification services. Janssen disabled for all platforms\"\n            return False\n\n       .jans.server_uri =.jans.conf[\"server_uri\"]\n        notifyClientFactory = NotifyClientFactory.instance()\n        metadataConfiguration = None\n        try:\n            metadataConfiguration = notifyClientFactory.createMetaDataConfigurationService.jans.server_uri).getMetadataConfiguration()\n        except:\n            print \"Super-Janssen. Initialize Janssen notification services. Failed to load metadata. Exception: \", sys.exc_info()[1]\n            return False\n\n       .jans.lient = notifyClientFactory.createNotifyService(metadataConfiguration)\n        encryptionService = CdiUtil.bean(EncryptionService)\n\n        if android_creds[\"enabled\"]:\n           .jans.access_key = android_creds[\"access_key\"]\n           .jans.secret_access_key = android_creds[\"secret_access_key\"]\n    \n            try:\n               .jans.secret_access_key = encryptionService.decrypt.jans.secret_access_key)\n            except:\n                # Ignore exception. Password is not encrypted\n                print \"Super-Janssen. Initialize Janssen notification services. Assuming that .jans.secret_access_key' in not encrypted\"\n            \n            self.pushAndroidService =.jans.lient \n            self.pushAndroidServiceAuth = notifyClientFactory.getAuthorization.jans.access_key,.jans.secret_access_key);\n            print \"Super-Janssen. Initialize Janssen notification services. Created Android notification service\"\n\n        if ios_creds[\"enabled\"]:\n           .jans.access_key = ios_creds[\"access_key\"]\n           .jans.secret_access_key = ios_creds[\"secret_access_key\"]\n    \n            try:\n               .jans.secret_access_key = encryptionService.decrypt.jans.secret_access_key)\n            except:\n                # Ignore exception. Password is not encrypted\n                print \"Super-Janssen. Initialize Janssen notification services. Assuming that .jans.secret_access_key' in not encrypted\"\n            \n            self.pushAppleService =.jans.lient \n            self.pushAppleServiceAuth = notifyClientFactory.getAuthorization.jans.access_key,.jans.secret_access_key);\n            print \"Super-Janssen. Initialize Janssen notification services. Created iOS notification service\"\n\n        enabled = self.pushAndroidService != None or self.pushAppleService != None\n\n        return enabled\n\n    def loadPushNotificationCreds(self, configurationAttributes):\n        print \"Super-Janssen. Initialize notification services\"\n        if not configurationAttributes.containsKey(\"credentials_file\"):\n            return None\n\n        super.jans.creds_file = configurationAttributes.get(\"credentials_file\").getValue2()\n\n        # Load credentials from file\n        f = open(super.jans.creds_file, 'r')\n        try:\n            creds = json.loads(f.read())\n        except:\n            print \"Super-Janssen. Initialize notification services. Failed to load credentials from file:\", super.jans.creds_file\n            return None\n        finally:\n            f.close()\n\n        return creds\n\n    def sendPushNotification(self, client_redirect_uri, user, super.jans.request):\n        try:\n            self.sendPushNotificationImpl(client_redirect_uri, user, super.jans.request)\n        except:\n            print \"Super-Janssen. Send push notification. Failed to send push notification: \", sys.exc_info()[1]\n\n    def sendPushNotificationImpl(self, client_redirect_uri, user, super.jans.request):\n        if not self.enabledPushNotifications:\n            return\n\n        user_name = user.getUserId()\n        print \"Super-Janssen. Send push notification. Loading user '%s' devices\" % user_name\n\n        send_notification = False\n        send_notification_result = True\n\n        userService = CdiUtil.bean(UserService)\n        deviceRegistrationService = CdiUtil.bean(DeviceRegistrationService)\n\n        user_inum = userService.getUserInum(user_name)\n\n        send_android = 0\n        send_ios = 0\n        u2f_devices_list = deviceRegistrationService.findUserDeviceRegistrations(user_inum, client_redirect_uri, \"oxId\", \"oxDeviceData\", \"oxDeviceNotificationConf\")\n        if u2f_devices_list.size() > 0:\n            for u2f_device in u2f_devices_list:\n                device_data = u2f_device.getDeviceData()\n\n                # Device data which Super-Janssen gets during enrollment\n                if device_data == None:\n                    continue\n\n                platform = device_data.getPlatform()\n                push_token = device_data.getPushToken()\n                debug = False\n\n                if StringHelper.equalsIgnoreCase(platform, \"ios\") and StringHelper.isNotEmpty(push_token):\n                    # Sending notification to iOS user's device\n                    if self.pushAppleService == None:\n                        print \"Super-Janssen. Send push notification. Apple native push notification service is not enabled\"\n                    else:\n                        send_notification = True\n                        \n                        title = \"Super Janssen\"\n                        message = \"Confirm your sign in request to: %s\" % client_redirect_uri\n\n                        if self.pushSnsMode or self.pushJanssenMode:\n                            pushSnsService = CdiUtil.bean(PushSnsService)\n                            targetEndpointArn = self.getTargetEndpointArn(deviceRegistrationService, pushSnsService, PushPlatform.APNS, user, u2f_device)\n                            if targetEndpointArn == None:\n                            \treturn\n\n                            send_notification = True\n    \n                            sns_push_request_dictionary = { \"aps\": \n                                                                { \"badge\": 0,\n                                                                  \"alert\" : {\"body\": message, \"title\" : title},\n                                                                  \"category\": \"ACTIONABLE\",\n                                                                  \"content-available\": \"1\",\n                                                                  \"sound\": 'default'\n                                                           },\n                                                           \"request\" : super.jans.request\n                            }\n                            push_message = json.dumps(sns_push_request_dictionary, separators=(',',':'))\n    \n                            if self.pushSnsMode:\n                                apple_push_platform = PushPlatform.APNS\n                                if not self.pushAppleServiceProduction:\n                                    apple_push_platform = PushPlatform.APNS_SANDBOX\n        \n                                send_notification_result = pushSnsService.sendPushMessage(self.pushAppleService, apple_push_platform, targetEndpointArn, push_message, None)\n                                if debug:\n                                    print \"Super-Janssen. Send iOS SNS push notification. token: '%s', message: '%s', send_notification_result: '%s', apple_push_platform: '%s'\" % (push_token, push_message, send_notification_result, apple_push_platform)\n                            elif self.pushJanssenMode:\n                                send_notification_result = self.pushAppleService.sendNotification(self.pushAppleServiceAuth, targetEndpointArn, push_message)\n                                if debug:\n                                    print \"Super-Janssen. Send iOS Janssen push notification. token: '%s', message: '%s', send_notification_result: '%s'\" % (push_token, push_message, send_notification_result)\n                        else:\n                            additional_fields = { \"request\" : super.jans.request }\n    \n                            msgBuilder = APNS.newPayload().alertBody(message).alertTitle(title).sound(\"default\")\n                            msgBuilder.category('ACTIONABLE').badge(0)\n                            msgBuilder.forNewsstand()\n                            msgBuilder.customFields(additional_fields)\n                            push_message = msgBuilder.build()\n    \n                            send_notification_result = self.pushAppleService.push(push_token, push_message)\n                            if debug:\n                                print \"Super-Janssen. Send iOS Native push notification. token: '%s', message: '%s', send_notification_result: '%s'\" % (push_token, push_message, send_notification_result)\n                        send_ios = send_ios + 1\n\n                if StringHelper.equalsIgnoreCase(platform, \"android\") and StringHelper.isNotEmpty(push_token):\n                    # Sending notification to Android user's device\n                    if self.pushAndroidService == None:\n                        print \"Super-Janssen. Send native push notification. Android native push notification service is not enabled\"\n                    else:\n                        send_notification = True\n\n                        title = \"Super-Janssen\"\n                        if self.pushSnsMode or self.pushJanssenMode:\n                            pushSnsService = CdiUtil.bean(PushSnsService)\n                            targetEndpointArn = self.getTargetEndpointArn(deviceRegistrationService, pushSnsService, PushPlatform.GCM, user, u2f_device)\n                            if targetEndpointArn == None:\n                            \treturn\n\n                            send_notification = True\n    \n                            sns_push_request_dictionary = { \"collapse_key\": \"single\",\n                                                            \"content_available\": True,\n                                                            \"time_to_live\": 60,\n                                                            \"data\": \n                                                                { \"message\" : super.jans.request,\n                                                                  \"title\" : title }\n                            }\n                            push_message = json.dumps(sns_push_request_dictionary, separators=(',',':'))\n    \n                            if self.pushSnsMode:\n                                send_notification_result = pushSnsService.sendPushMessage(self.pushAndroidService, PushPlatform.GCM, targetEndpointArn, push_message, None)\n                                if debug:\n                                    print \"Super-Janssen. Send Android SNS push notification. token: '%s', message: '%s', send_notification_result: '%s'\" % (push_token, push_message, send_notification_result)\n                            elif self.pushJanssenMode:\n                                send_notification_result = self.pushAndroidService.sendNotification(self.pushAndroidServiceAuth, targetEndpointArn, push_message)\n                                if debug:\n                                    print \"Super-Janssen. Send Android Janssen push notification. token: '%s', message: '%s', send_notification_result: '%s'\" % (push_token, push_message, send_notification_result)\n                        else:\n                            msgBuilder = Message.Builder().addData(\"message\", super.jans.request).addData(\"title\", title).collapseKey(\"single\").contentAvailable(True)\n                            push_message = msgBuilder.build()\n    \n                            send_notification_result = self.pushAndroidService.send(push_message, push_token, 3)\n                            if debug:\n                                print \"Super-Janssen. Send Android Native push notification. token: '%s', message: '%s', send_notification_result: '%s'\" % (push_token, push_message, send_notification_result)\n                        send_android = send_android + 1\n\n        print \"Super-Janssen. Send push notification. send_android: '%s', send_ios: '%s'\" % (send_android, send_ios)\n\n    def getTargetEndpointArn(self, deviceRegistrationService, pushSnsService, platform, user, u2fDevice):\n        targetEndpointArn = None\n                             \n        # Return endpoint ARN if it created already\n        notificationConf = u2fDevice.getDeviceNotificationConf()\n        if StringHelper.isNotEmpty(notificationConf):\n            notificationConfJson = json.loads(notificationConf)\n            targetEndpointArn = notificationConfJson['sns_endpoint_arn']\n            if StringHelper.isNotEmpty(targetEndpointArn):\n                print \"Super-Janssen. Get target endpoint ARN. There is already created target endpoint ARN\"\n                return targetEndpointArn\n\n        # Create endpoint ARN        \n        pushClient = None\n        pushClientAuth = None\n        platformApplicationArn = None\n        if platform == PushPlatform.GCM:\n            pushClient = self.pushAndroidService\n            if self.pushSnsMode:\n                platformApplicationArn = self.pushAndroidPlatformArn\n            if self.pushJanssenMode:\n                pushClientAuth = self.pushAndroidServiceAuth\n        elif platform == PushPlatform.APNS:\n            pushClient = self.pushAppleService\n            if self.pushSnsMode:\n                platformApplicationArn = self.pushApplePlatformArn\n            if self.pushJanssenMode:\n                pushClientAuth = self.pushAppleServiceAuth\n        else:\n            return None\n\n        deviceData = u2fDevice.getDeviceData()\n        pushToken = deviceData.getPushToken()\n        \n        print \"Super-Janssen. Get target endpoint ARN. Attempting to create target endpoint ARN for user: '%s'\" % user.getUserId()\n        if self.pushSnsMode:\n            targetEndpointArn = pushSnsService.createPlatformArn(pushClient, platformApplicationArn, pushToken, user)\n        else:\n            customUserData = pushSnsService.getCustomUserData(user)\n            registerDeviceResponse = pushClient.registerDevice(pushClientAuth, pushToken, customUserData);\n            if registerDeviceResponse != None and registerDeviceResponse.getStatusCode() == 200:\n                targetEndpointArn = registerDeviceResponse.getEndpointArn()\n        \n        if StringHelper.isEmpty(targetEndpointArn):\n\t        print \"Super-Janssen. Failed to get endpoint ARN for user: '%s'\" % user.getUserId()\n        \treturn None\n\n        print \"Super-Janssen. Get target endpoint ARN. Create target endpoint ARN '%s' for user: '%s'\" % (targetEndpointArn, user.getUserId())\n        \n        # Store created endpoint ARN in device entry\n        userInum = user.getAttribute(\"inum\")\n        u2fDeviceUpdate = deviceRegistrationService.findUserDeviceRegistration(userInum, u2fDevice.getId())\n        u2fDeviceUpdate.setDeviceNotificationConf('{\"sns_endpoint_arn\" : \"%s\"}' % targetEndpointArn)\n        deviceRegistrationService.updateDeviceRegistration(userInum, u2fDeviceUpdate)\n\n        return targetEndpointArn\n\n    def getApplicationUri(self, session_attributes):\n        if self.applicationId != None:\n            return self.applicationId\n            \n        if not session_attributes.containsKey(\"redirect_uri\"):\n            return None\n\n        return session_attributes.get(\"redirect_uri\")\n\n    def setRequestScopedParameters(self, identity, step):\n        downloadMap = HashMap()\n        if self.registrationUri != None:\n            identity.setWorkingParameter(\"external_registration_uri\", self.registrationUri)\n\n        if self.androidUrl!= None and step == 1:\n            downloadMap.put(\"android\", self.androidUrl)\n\n        if self.IOSUrl  != None and step == 1:\n            downloadMap.put(\"ios\", self.IOSUrl)\n            \n        if self.customLabel != None:\n            identity.setWorkingParameter(\"super.jans.label\", self.customLabel)\n            \n        identity.setWorkingParameter(\"download_url\", downloadMap)\n        identity.setWorkingParameter(\"super.jans.qr_options\", self.customQrOptions)\n\n    def addGeolocationData(self, session_attributes, super.jans.request_dictionary):\n        if session_attributes.containsKey(\"remote_ip\"):\n            remote_ip = session_attributes.get(\"remote_ip\")\n            if StringHelper.isNotEmpty(remote_ip):\n                print \"Super-Janssen. Prepare for step 2. Adding req_ip and req_loc to super.jans.request\"\n                super.jans.request_dictionary['req_ip'] = remote_ip\n\n                remote_loc_dic = self.determineGeolocationData(remote_ip)\n                if remote_loc_dic == None:\n                    print \"Super-Janssen. Prepare for step 2. Failed to determine remote location by remote IP '%s'\" % remote_ip\n                    return\n\n                remote_loc = \"%s, %s, %s\" % ( remote_loc_dic['country'], remote_loc_dic['regionName'], remote_loc_dic['city'] )\n                remote_loc_encoded = urllib.quote(remote_loc.encode('utf-8'))\n                super.jans.request_dictionary['req_loc'] = remote_loc_encoded\n\n    def determineGeolocationData(self, remote_ip):\n        print \"Super-Janssen. Determine remote location. remote_ip: '%s'\" % remote_ip\n        httpService = CdiUtil.bean(HttpService)\n\n        http_client = httpService.getHttpsClient()\n        http_client_params = http_client.getParams()\n        http_client_params.setIntParameter(CoreConnectionPNames.CONNECTION_TIMEOUT, 15 * 1000)\n        \n        geolocation_service_url = \"http://ip-api.com/json/%s?fields=49177\" % remote_ip\n        geolocation_service_headers = { \"Accept\" : \"application/json\" }\n\n        try:\n            http_service_response = httpService.executeGet(http_client, geolocation_service_url,  geolocation_service_headers)\n            http_response = http_service_response.getHttpResponse()\n        except:\n            print \"Super-Janssen. Determine remote location. Exception: \", sys.exc_info()[1]\n            return None\n\n        try:\n            if not httpService.isResponseStastusCodeOk(http_response):\n                print \"Super-Janssen. Determine remote location. Get invalid response from validation server: \", str(http_response.getStatusLine().getStatusCode())\n                httpService.consume(http_response)\n                return None\n    \n            response_bytes = httpService.getResponseContent(http_response)\n            response_string = httpService.convertEntityToString(response_bytes)\n            httpService.consume(http_response)\n        finally:\n            http_service_response.closeConnection()\n\n        if response_string == None:\n            print \"Super-Janssen. Determine remote location. Get empty response from location server\"\n            return None\n        \n        response = json.loads(response_string)\n        \n        if not StringHelper.equalsIgnoreCase(response['status'], \"success\"):\n            print \"Super-Janssen. Determine remote location. Get response with status: '%s'\" % response['status']\n            return None\n\n        return response\n\n    def isUserMemberOfGroup(self, user, attribute, group):\n        is_member = False\n        member_of_list = user.getAttributeValues(attribute)\n        if (member_of_list != None):\n            for member_of in member_of_list:\n                if StringHelper.equalsIgnoreCase(group, member_of) or member_of.endswith(group):\n                    is_member = True\n                    break\n\n        return is_member\n\n    def processAuditGroup(self, user, attribute, group):\n        is_member = self.isUserMemberOfGroup(user, attribute, group)\n        if (is_member):\n            print \"Super-Janssen. Authenticate for processAuditGroup. User '%s' member of audit group\" % user.getUserId()\n            print \"Super-Janssen. Authenticate for processAuditGroup. Sending e-mail about user '%s' login to %s\" % (user.getUserId(), self.audit_email)\n            \n            # Send e-mail to administrator\n            user_id = user.getUserId()\n            mailService = CdiUtil.bean(MailService)\n            subject = \"User log in: %s\" % user_id\n            body = \"User log in: %s\" % user_id\n            mailService.sendMail(self.audit_email, subject, body)\n", "person_authentication", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansConfProperty`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("92F0-759E", "jansCustomScr", "inum=92F0-759E,ou=scripts,o=jans", "ThumbSignIn authentication module", "thumb_sign_in", '[{"value1": "ts_host", "value2": "https://ts.host.com", "description": ""}, {"value1": "ts_apiKey", "value2": "ts_api_key", "description": ""}, {"value1": "ts_apiSecret", "value2": "ts_api_secret", "description": ""}]', 0, '[{"value1": "location_type", "value2": "ldap", "description": ""}, {"value1": "usage_type", "value2": "interactive", "description": ""}]', 1, "# Author: ThumbSignIn\n\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.as.server.security import Identity\nfrom io.jans.model.custom.script.type.auth import PersonAuthenticationType\nfrom io.jans.as.server.service import AuthenticationService\nfrom io.jans.util import StringHelper\nfrom io.jans.as.util import ServerUtil\nfrom com.pramati.ts.thumbsignin_java_sdk import ThumbsigninApiController\nfrom org.json import JSONObject\nfrom io.jans.as.model.util import Base64Util\nfrom java.lang import String\n\nimport java\n\n\nclass PersonAuthentication(PersonAuthenticationType):\n\n    def __init__(self, current_time_millis):\n        self.currentTimeMillis = current_time_millis\n        self.thumbsigninApiController = ThumbsigninApiController()\n\n    def init(self, customScript, configuration_attributes):\n        print \"ThumbSignIn. Initialization\"\n\n        global ts_host\n        ts_host = configuration_attributes.get(\"ts_host\").getValue2()\n        print \"ThumbSignIn. Initialization. Value of ts_host is %s\" % ts_host\n\n        global ts_api_key\n        ts_api_key = configuration_attributes.get(\"ts_apiKey\").getValue2()\n        print \"ThumbSignIn. Initialization. Value of ts_api_key is %s\" % ts_api_key\n\n        global ts_api_secret\n        ts_api_secret = configuration_attributes.get(\"ts_apiSecret\").getValue2()\n\n        global ts_statusPath\n        ts_statusPath = \"/ts/secure/txn-status/\"\n\n        global AUTHENTICATE\n        AUTHENTICATE = \"authenticate\"\n\n        global REGISTER\n        REGISTER = \"register\"\n\n        global TRANSACTION_ID\n        TRANSACTION_ID = \"transactionId\"\n\n        global USER_ID\n        USER_ID = \"userId\"\n\n        global USER_LOGIN_FLOW\n        USER_LOGIN_FLOW = \"userLoginFlow\"\n\n        global THUMBSIGNIN_AUTHENTICATION\n        THUMBSIGNIN_AUTHENTICATION = \"ThumbSignIn_Authentication\"\n\n        global THUMBSIGNIN_REGISTRATION\n        THUMBSIGNIN_REGISTRATION = \"ThumbSignIn_Registration\"\n\n        global THUMBSIGNIN_LOGIN_POST_REGISTRATION\n        THUMBSIGNIN_LOGIN_POST_REGISTRATION = \"ThumbSignIn_RegistrationSucess\"\n\n        global RELYING_PARTY_ID\n        RELYING_PARTY_ID = \"relyingPartyId\"\n\n        global RELYING_PARTY_LOGIN_URL\n        RELYING_PARTY_LOGIN_URL = \"relyingPartyLoginUrl\"\n\n        global TSI_LOGIN_PAGE\n        TSI_LOGIN_PAGE = \"/auth/thumbsignin/tsLogin.xhtml\"\n\n        global TSI_REGISTER_PAGE\n        TSI_REGISTER_PAGE = \"/auth/thumbsignin/tsRegister.xhtml\"\n\n        global TSI_LOGIN_POST_REGISTRATION_PAGE\n        TSI_LOGIN_POST_REGISTRATION_PAGE = \"/auth/thumbsignin/tsRegistrationSuccess.xhtml\"\n\n        print \"ThumbSignIn. Initialized successfully\"\n        return True\n\n    @staticmethod\n    def set_relying_party_login_url(identity):\n        print \"ThumbSignIn. Inside set_relying_party_login_url...\"\n        session_id = identity.getSessionId()\n        session_attribute = session_id.getSessionAttributes()\n        state_jwt_token = session_attribute.get(\"state\")\n        print \"ThumbSignIn. Value of state_jwt_token is %s\" % state_jwt_token\n        relying_party_login_url = \"\"\n        if (state_jwt_token is None) or (\".\" not in state_jwt_token):\n            print \"ThumbSignIn. Value of state parameter is not in the format of JWT Token\"\n            identity.setWorkingParameter(RELYING_PARTY_LOGIN_URL, relying_party_login_url)\n            return None\n\n        state_jwt_token_array = String(state_jwt_token).split(\"\\\\.\")\n        state_jwt_token_payload = state_jwt_token_array[1]\n        state_payload_str = String(Base64Util.base64urldecode(state_jwt_token_payload), \"UTF-8\")\n        state_payload_json = JSONObject(state_payload_str)\n        print \"ThumbSignIn. Value of state JWT token Payload is %s\" % state_payload_json\n        if state_payload_json.has(\"additional_claims\"):\n            additional_claims = state_payload_json.get(\"additional_claims\")\n            relying_party_id = additional_claims.get(RELYING_PARTY_ID)\n            print \"ThumbSignIn. Value of relying_party_id is %s\" % relying_party_id\n            identity.setWorkingParameter(RELYING_PARTY_ID, relying_party_id)\n\n            if String(relying_party_id).startsWith(\"google.com\"):\n                # google.com/a/unphishableenterprise.com\n                relying_party_id_array = String(relying_party_id).split(\"/\")\n                google_domain = relying_party_id_array[2]\n                print \"ThumbSignIn. Value of google_domain is %s\" % google_domain\n                relying_party_login_url = \"https://www.google.com/accounts/AccountChooser?hd=\"+ google_domain + \"%26continue=https://apps.google.com/user/hub\"\n                # elif (String(relying_party_id).startsWith(\"xyz\")):\n                # relying_party_login_url = \"xyz.com\"\n            else:\n                # If relying_party_login_url is empty, Janssen's default login URL will be used\n                relying_party_login_url = \"\"\n\n        print \"ThumbSignIn. Value of relying_party_login_url is %s\" % relying_party_login_url\n        identity.setWorkingParameter(RELYING_PARTY_LOGIN_URL, relying_party_login_url)\n        return None\n\n    def initialize_thumbsignin(self, identity, request_path):\n        # Invoking the authenticate/register ThumbSignIn API via the Java SDK\n        thumbsignin_response = self.thumbsigninApiController.handleThumbSigninRequest(request_path, ts_api_key, ts_api_secret)\n        print \"ThumbSignIn. Value of thumbsignin_response is %s\" % thumbsignin_response\n\n        thumbsignin_response_json = JSONObject(thumbsignin_response)\n        transaction_id = thumbsignin_response_json.get(TRANSACTION_ID)\n        status_request_type = \"authStatus\" if request_path == AUTHENTICATE else \"regStatus\"\n        status_request = status_request_type + \"/\" + transaction_id\n        print \"ThumbSignIn. Value of status_request is %s\" % status_request\n\n        authorization_header = self.thumbsigninApiController.getAuthorizationHeaderJsonStr(status_request, ts_api_key, ts_api_secret)\n        print \"ThumbSignIn. Value of authorization_header is %s\" % authorization_header\n        # {\"authHeader\":\"HmacSHA256 Credential=X,SignedHeaders=accept;content-type;x-ts-date,Signature=X\",\"XTsDate\":\"X\"}\n        authorization_header_json = JSONObject(authorization_header)\n        auth_header = authorization_header_json.get(\"authHeader\")\n        x_ts_date = authorization_header_json.get(\"XTsDate\")\n\n        tsi_response_key = \"authenticateResponseJsonStr\" if request_path == AUTHENTICATE else \"registerResponseJsonStr\"\n        identity.setWorkingParameter(tsi_response_key, thumbsignin_response)\n        identity.setWorkingParameter(\"authorizationHeader\", auth_header)\n        identity.setWorkingParameter(\"xTsDate\", x_ts_date)\n        return None\n\n    def prepareForStep(self, configuration_attributes, request_parameters, step):\n        print \"ThumbSignIn. Inside prepareForStep. Step %d\" % step\n        identity = CdiUtil.bean(Identity)\n        authentication_service = CdiUtil.bean(AuthenticationService)\n\n        identity.setWorkingParameter(\"ts_host\", ts_host)\n        identity.setWorkingParameter(\"ts_statusPath\", ts_statusPath)\n\n        self.set_relying_party_login_url(identity)\n\n        if step == 1 or step == 3:\n            print \"ThumbSignIn. Prepare for step 1\"\n            self.initialize_thumbsignin(identity, AUTHENTICATE)\n            return True\n\n        elif step == 2:\n            print \"ThumbSignIn. Prepare for step 2\"\n            if identity.isSetWorkingParameter(USER_LOGIN_FLOW):\n                user_login_flow = identity.getWorkingParameter(USER_LOGIN_FLOW)\n                print \"ThumbSignIn. Value of user_login_flow is %s\" % user_login_flow\n            user = authentication_service.getAuthenticatedUser()\n            if user is None:\n                print \"ThumbSignIn. Prepare for step 2. Failed to determine user name\"\n                return False\n            user_name = user.getUserId()\n            print \"ThumbSignIn. Prepare for step 2. user_name: \" + user_name\n            if user_name is None:\n                return False\n            identity.setWorkingParameter(USER_ID, user_name)\n            self.initialize_thumbsignin(identity, REGISTER + \"/\" + user_name)\n            return True\n        else:\n            return False\n\n    def get_user_id_from_thumbsignin(self, request_parameters):\n        transaction_id = ServerUtil.getFirstValue(request_parameters, TRANSACTION_ID)\n        print \"ThumbSignIn. Value of transaction_id is %s\" % transaction_id\n        get_user_request = \"getUser/\" + transaction_id\n        print \"ThumbSignIn. Value of get_user_request is %s\" % get_user_request\n\n        get_user_response = self.thumbsigninApiController.handleThumbSigninRequest(get_user_request, ts_api_key, ts_api_secret)\n        print \"ThumbSignIn. Value of get_user_response is %s\" % get_user_response\n        get_user_response_json = JSONObject(get_user_response)\n        thumbsignin_user_id = get_user_response_json.get(USER_ID)\n        print \"ThumbSignIn. Value of thumbsignin_user_id is %s\" % thumbsignin_user_id\n        return thumbsignin_user_id\n\n    def authenticate(self, configuration_attributes, request_parameters, step):\n        print \"ThumbSignIn. Inside authenticate. Step %d\" % step\n        authentication_service = CdiUtil.bean(AuthenticationService)\n        identity = CdiUtil.bean(Identity)\n\n        identity.setWorkingParameter(\"ts_host\", ts_host)\n        identity.setWorkingParameter(\"ts_statusPath\", ts_statusPath)\n\n        if step == 1 or step == 3:\n            print \"ThumbSignIn. Authenticate for Step %d\" % step\n\n            login_flow = ServerUtil.getFirstValue(request_parameters, \"login_flow\")\n            print \"ThumbSignIn. Value of login_flow parameter is %s\" % login_flow\n\n            # Logic for ThumbSignIn Authentication Flow (Either step 1 or step 3)\n            if login_flow == THUMBSIGNIN_AUTHENTICATION or login_flow == THUMBSIGNIN_LOGIN_POST_REGISTRATION:\n                identity.setWorkingParameter(USER_LOGIN_FLOW, login_flow)\n                print \"ThumbSignIn. Value of userLoginFlow is %s\" % identity.getWorkingParameter(USER_LOGIN_FLOW)\n                logged_in_status = authentication_service.authenticate(self.get_user_id_from_thumbsignin(request_parameters))\n                print \"ThumbSignIn. logged_in status : %r\" % logged_in_status\n                return logged_in_status\n\n            # Logic for traditional login flow (step 1)\n            print \"ThumbSignIn. User credentials login flow\"\n            identity.setWorkingParameter(USER_LOGIN_FLOW, THUMBSIGNIN_REGISTRATION)\n            print \"ThumbSignIn. Value of userLoginFlow is %s\" % identity.getWorkingParameter(USER_LOGIN_FLOW)\n            logged_in = self.authenticate_user_credentials(identity, authentication_service)\n            print \"ThumbSignIn. Status of User Credentials based Authentication : %r\" % logged_in\n\n            # When the traditional login fails, reinitialize the ThumbSignIn data before sending error response to UI\n            if not logged_in:\n                self.initialize_thumbsignin(identity, AUTHENTICATE)\n                return False\n\n            print \"ThumbSignIn. Authenticate successful for step %d\" % step\n            return True\n\n        elif step == 2:\n            print \"ThumbSignIn. Registration flow (step 2)\"\n            self.verify_user_login_flow(identity)\n\n            user = self.get_authenticated_user_from.jans.authentication_service)\n            if user is None:\n                print \"ThumbSignIn. Registration flow (step 2). Failed to determine user name\"\n                return False\n\n            user_name = user.getUserId()\n            print \"ThumbSignIn. Registration flow (step 2) successful. user_name: %s\" % user_name\n            return True\n\n        else:\n            return False\n\n    def authenticate_user_credentials(self, identity, authentication_service):\n        credentials = identity.getCredentials()\n        user_name = credentials.getUsername()\n        user_password = credentials.getPassword()\n        print \"ThumbSignIn. user_name: \" + user_name\n        logged_in = False\n        if StringHelper.isNotEmptyString(user_name) and StringHelper.isNotEmptyString(user_password):\n            logged_in = self.authenticate_user_in.jans.ldap(authentication_service, user_name, user_password)\n        return logged_in\n\n    @staticmethod\n    def authenticate_user_in.jans.ldap(authentication_service, user_name, user_password):\n        return authentication_service.authenticate(user_name, user_password)\n\n    @staticmethod\n    def get_authenticated_user_from.jans.authentication_service):\n        return authentication_service.getAuthenticatedUser()\n\n    @staticmethod\n    def verify_user_login_flow(identity):\n        if identity.isSetWorkingParameter(USER_LOGIN_FLOW):\n            user_login_flow = identity.getWorkingParameter(USER_LOGIN_FLOW)\n            print \"ThumbSignIn. Value of user_login_flow is %s\" % user_login_flow\n        else:\n            identity.setWorkingParameter(USER_LOGIN_FLOW, THUMBSIGNIN_REGISTRATION)\n            print \"ThumbSignIn. Setting the value of user_login_flow to %s\" % identity.getWorkingParameter(USER_LOGIN_FLOW)\n\n    def getExtraParametersForStep(self, configuration_attributes, step):\n        return None\n\n    def getCountAuthenticationSteps(self, configuration_attributes):\n        print \"ThumbSignIn. Inside getCountAuthenticationSteps..\"\n        identity = CdiUtil.bean(Identity)\n\n        user_login_flow = identity.getWorkingParameter(USER_LOGIN_FLOW)\n        print \"ThumbSignIn. Value of userLoginFlow is %s\" % user_login_flow\n        if user_login_flow == THUMBSIGNIN_AUTHENTICATION:\n            print \"ThumbSignIn. Total Authentication Steps is: 1\"\n            return 1\n        print \"ThumbSignIn. Total Authentication Steps is: 3\"\n        return 3\n\n    def getPageForStep(self, configuration_attributes, step):\n        print \"ThumbSignIn. Inside getPageForStep. Step %d\" % step\n        if step == 3:\n            return TSI_LOGIN_POST_REGISTRATION_PAGE\n        thumbsignin_page = TSI_REGISTER_PAGE if step == 2 else TSI_LOGIN_PAGE\n        return thumbsignin_page\n\n    def destroy(self, configurationAttributes):\n        print \"ThumbSignIn. Destroy\"\n        return True\n\n    def getApiVersion(self):\n        return 11\n        \n    def getAuthenticationMethodClaims(self, requestParameters):\n        return None\n        \n    def isValidAuthenticationMethod(self, usageType, configurationAttributes):\n        return True\n\n    def getAlternativeAuthenticationMethod(self, usageType, configurationAttributes):\n        return None\n\n    def getNextStep(self, configurationAttributes, requestParameters, step):\n        return -1\n\n    def getLogoutExternalUrl(self, configurationAttributes, requestParameters):\n        print \"Get external logout URL call\"\n        return None\n\n    def logout(self, configurationAttributes, requestParameters):\n        return True\n", "person_authentication", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("A51E-76DA", "jansCustomScr", "inum=A51E-76DA,ou=scripts,o=jans", "Sample authentication module", "basic", 0, '[{"value1": "usage_type", "value2": "interactive", "description": ""}, {"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2016, Janssen\n#\n# Author: Yuriy Movchan\n#\n\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.as.server.security import Identity\nfrom io.jans.model.custom.script.type.auth import PersonAuthenticationType\nfrom io.jans.as.server.service import AuthenticationService\nfrom io.jans.util import StringHelper\n\nimport java\n\nclass PersonAuthentication(PersonAuthenticationType):\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript,  configurationAttributes):\n        print \"Basic. Initialization\"\n        print \"Basic. Initialized successfully\"\n        return True   \n\n    def destroy(self, configurationAttributes):\n        print \"Basic. Destroy\"\n        print \"Basic. Destroyed successfully\"\n        return True\n        \n    def getAuthenticationMethodClaims(self, requestParameters):\n        return None\n        \n    def getApiVersion(self):\n        return 11\n\n    def isValidAuthenticationMethod(self, usageType, configurationAttributes):\n        return True\n\n    def getAlternativeAuthenticationMethod(self, usageType, configurationAttributes):\n        return None\n\n    def authenticate(self, configurationAttributes, requestParameters, step):\n        authenticationService = CdiUtil.bean(AuthenticationService)\n\n        if (step == 1):\n            print \"Basic. Authenticate for step 1\"\n\n            identity = CdiUtil.bean(Identity)\n            credentials = identity.getCredentials()\n\n            user_name = credentials.getUsername()\n            user_password = credentials.getPassword()\n\n            logged_in = False\n            if (StringHelper.isNotEmptyString(user_name) and StringHelper.isNotEmptyString(user_password)):\n                logged_in = authenticationService.authenticate(user_name, user_password)\n\n            if (not logged_in):\n                return False\n\n            return True\n        else:\n            return False\n\n    def prepareForStep(self, configurationAttributes, requestParameters, step):\n        if (step == 1):\n            print \"Basic. Prepare for Step 1\"\n            return True\n        else:\n            return False\n\n    def getExtraParametersForStep(self, configurationAttributes, step):\n        return None\n\n    def getCountAuthenticationSteps(self, configurationAttributes):\n        return 1\n\n    def getPageForStep(self, configurationAttributes, step):\n        return \"\"\n\n    def getNextStep(self, configurationAttributes, requestParameters, step):\n        return -1\n\n    def getLogoutExternalUrl(self, configurationAttributes, requestParameters):\n        print \"Get external logout URL call\"\n        return None\n\n    def logout(self, configurationAttributes, requestParameters):\n        return True\n", "person_authentication", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansConfProperty`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("A910-56AB", "jansCustomScr", "inum=A910-56AB,ou=scripts,o=jans", "Sample script for SCIM events", "scim_event_handler", '[{"value1": "testProp1", "value2": "Test value 1", "description": ""}, {"value1": "testProp2", "value2": "Test value 2", "description": ""}]', 0, '[{"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxTrust is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2014, Janssen\n#\n# Author: Jose Gonzalez\n#\nfrom io.jans.model.custom.script.type.scim import ScimType\nfrom io.jans.util import StringHelper, ArrayHelper\nfrom java.util import Arrays, ArrayList\nfrom io.jans.oxtrust.ldap.service import PersonService\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.oxtrust.model import JanssenCustomPerson\n\nimport java\n\nclass ScimEventHandler(ScimType):\n\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript, configurationAttributes):\n        print \"ScimEventHandler (init): Initialized successfully\"\n        return True   \n\n    def destroy(self, configurationAttributes):\n        print \"ScimEventHandler (destroy): Destroyed successfully\"\n        return True   \n\n    def getApiVersion(self):\n        #return 2 if you want the post* scripts being executed\n        return 11\n\n    def createUser(self, user, configurationAttributes):\n\n        print \"ScimEventHandler (createUser): Current id = \" + user.getUid()\n\n        testProp1 = configurationAttributes.get(\"testProp1\").getValue2()\n        testProp2 = configurationAttributes.get(\"testProp2\").getValue2()\n\n        print \"ScimEventHandler (createUser): testProp1 = \" + testProp1\n        print \"ScimEventHandler (createUser): testProp2 = \" + testProp2\n\n        return True\n\n    def updateUser(self, user, configurationAttributes):\n        personService = CdiUtil.bean(PersonService)\n        oldUser = personService.getPersonByUid(user.getUid())\n        print \"ScimEventHandler (updateUser): Old displayName %s\" % oldUser.getDisplayName()\n        print \"ScimEventHandler (updateUser): New displayName \" + user.getDisplayName()\n        return True\n\n    def deleteUser(self, user, configurationAttributes):\n        print \"ScimEventHandler (deleteUser): Current id = \" + user.getUid()\n        return True\n\n    def createGroup(self, group, configurationAttributes):\n        print \"ScimEventHandler (createGroup): Current displayName = \" + group.getDisplayName()\n        return True\n\n    def updateGroup(self, group, configurationAttributes):\n        print \"ScimEventHandler (updateGroup): Current displayName = \" + group.getDisplayName()\n        return True\n\n    def deleteGroup(self, group, configurationAttributes):\n        print \"ScimEventHandler (deleteGroup): Current displayName = \" + group.getDisplayName()\n        return True\n        \n    def postCreateUser(self, user, configurationAttributes):\n        return True\n\n    def postUpdateUser(self, user, configurationAttributes):\n        return True\n\n    def postDeleteUser(self, user, configurationAttributes):\n        return True\n\n    def postUpdateGroup(self, group, configurationAttributes):\n        return True\n\n    def postCreateGroup(self, group, configurationAttributes):\n        return True\n\n    def postDeleteGroup(self, group, configurationAttributes):\n        return True", "scim", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansConfProperty`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("DAA9-B788", "jansCustomScr", "inum=DAA9-B788,ou=scripts,o=jans", "Sample Client Registration script", "client_registration", '[{"value1": "client_redirect_uris", "value2": "https://client.example.com/example1, https://client.example.com/example2", "description": ""}]', 0, '[{"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2016, Janssen\n#\n# Author: Yuriy Movchan\n#\n\nfrom io.jans.model.custom.script.type.client import ClientRegistrationType\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.as.service import ScopeService\nfrom io.jans.util import StringHelper, ArrayHelper\nfrom java.util import Arrays, ArrayList, HashSet\n\nimport java\n\nclass ClientRegistration(ClientRegistrationType):\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript, configurationAttributes):\n        print \"Client registration. Initialization\"\n        \n        self.clientRedirectUrisSet = self.prepareClientRedirectUris(configurationAttributes)\n\n        print \"Client registration. Initialized successfully\"\n        return True   \n\n    def destroy(self, configurationAttributes):\n        print \"Client registration. Destroy\"\n        print \"Client registration. Destroyed successfully\"\n        return True   \n\n    # Update client entry before persistent it\n    #   registerRequest is io.jans.as.client.RegisterRequest\n    #   client is io.jans.as.model.registration.Client\n    #   configurationAttributes is java.util.Map<String, SimpleCustomProperty>\n    def createClient(self, registerRequest, client, configurationAttributes):\n        print \"Client registration. CreateClient method\"\n\n        redirectUris = client.getRedirectUris()\n        print \"Client registration. Redirect Uris: %s\" % redirectUris\n\n        addAddressScope = False\n        for redirectUri in redirectUris:\n            if (self.clientRedirectUrisSet.contains(redirectUri)):\n                addAddressScope = True\n                break\n        \n        print \"Client registration. Is add address scope: %s\" % addAddressScope\n\n        if addAddressScope:\n            currentScopes = client.getScopes()\n            print \"Client registration. Current scopes: %s\" % currentScopes\n            \n            scopeService = CdiUtil.bean(ScopeService)\n            addressScope = scopeService.getScopeByDisplayName(\"address\")\n            newScopes = ArrayHelper.addItemToStringArray(currentScopes, addressScope.getDn())\n    \n            print \"Client registration. Result scopes: %s\" % newScopes\n            client.setScopes(newScopes)\n\n        return True\n\n    # Update client entry before persistent it\n    #   registerRequest is io.jans.as.client.RegisterRequest\n    #   client is io.jans.as.model.registration.Client\n    #   configurationAttributes is java.util.Map<String, SimpleCustomProperty>\n    def updateClient(self, registerRequest, client, configurationAttributes):\n        print \"Client registration. UpdateClient method\"\n        return True\n\n    def getApiVersion(self):\n        return 11\n\n    def prepareClientRedirectUris(self, configurationAttributes):\n        clientRedirectUrisSet = HashSet()\n        if not configurationAttributes.containsKey(\"client_redirect_uris\"):\n            return clientRedirectUrisSet\n\n        clientRedirectUrisList = configurationAttributes.get(\"client_redirect_uris\").getValue2()\n        if StringHelper.isEmpty(clientRedirectUrisList):\n            print \"Client registration. The property client_redirect_uris is empty\"\n            return clientRedirectUrisSet    \n\n        clientRedirectUrisArray = StringHelper.split(clientRedirectUrisList, \",\")\n        if ArrayHelper.isEmpty(clientRedirectUrisArray):\n            print \"Client registration. No clients specified in client_redirect_uris property\"\n            return clientRedirectUrisSet\n        \n        # Convert to HashSet to quick search\n        i = 0\n        count = len(clientRedirectUrisArray)\n        while i < count:\n            uris = clientRedirectUrisArray[i]\n            clientRedirectUrisSet.add(uris)\n            i = i + 1\n\n        return clientRedirectUrisSet\n\n    # Returns secret key which will be used to validate Software Statement if HMAC algorithm is used (e.g. HS256, HS512). Invoked if oxauth conf property softwareStatementValidationType=SCRIPT which is default/fallback value.\n    # context is reference of io.jans.as.service.external.context.DynamicClientRegistrationContext (in https://github.com/JanssenFederation/oxauth project )\n    def getSoftwareStatementHmacSecret(self, context):\n        return \"\"\n\n    # Returns JWKS which will be used to validate Software Statement if keys are used (e.g. RS256). Invoked if oxauth conf property softwareStatementValidationType=SCRIPT which is default/fallback value.\n    # context is reference of io.jans.as.service.external.context.DynamicClientRegistrationContext (in https://github.com/JanssenFederation/oxauth project )\n    def getSoftwareStatementJwks(self, context):\n        return \"\"\n", "client_registration", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansConfProperty`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("09A0-93D6", "jansCustomScr", "inum=09A0-93D6,ou=scripts,o=jans", "Twilio SMS authentication module", "twilio_sms", '[{"value1": "twilio_sid", "value2": "", "description": "Twilio account SID"}, {"value1": "twilio_token", "value2": "", "description": "Twilio API token"}, {"value1": "from_number", "value2": "", "description": "Twilio phone number with SMS capabilities"}]', 0, '[{"value1": "usage_type", "value2": "interactive", "description": ""}, {"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\r\n# Copyright (c) 2018, Janssen\r\n#\r\n# Author: Jose Gonzalez\r\n# Author: Gasmyr Mougang\r\n\r\nfrom io.jans.service.cdi.util import CdiUtil\r\nfrom io.jans.as.server.security import Identity\r\nfrom io.jans.model.custom.script.type.auth import PersonAuthenticationType\r\nfrom io.jans.as.server.service import AuthenticationService\r\nfrom io.jans.as.server.service import UserService\r\nfrom io.jans.as.server.service import SessionIdService\r\nfrom io.jans.as.util import ServerUtil\r\nfrom io.jans.util import StringHelper, ArrayHelper\r\nfrom java.util import Arrays\r\nfrom javax.faces.application import FacesMessage\r\nfrom io.jans.jsf2.message import FacesMessages\r\n\r\nimport com.twilio.Twilio as Twilio\r\nimport com.twilio.rest.api.v2010.account.Message as Message\r\nimport com.twilio.type.PhoneNumber as PhoneNumber\r\nimport org.codehaus.jettison.json.JSONArray as JSONArray\r\n\r\n\r\nimport java\r\nimport random\r\nimport jarray\r\n\r\nclass PersonAuthentication(PersonAuthenticationType):\r\n    def __init__(self, currentTimeMillis):\r\n        self.currentTimeMillis = currentTimeMillis\r\n        self.mobile_number = None\r\n        self.identity = CdiUtil.bean(Identity)\r\n\r\n    def init(self, customScript, configurationAttributes):\r\n        print \"==============================================\"\r\n        print \"===TWILIO SMS INITIALIZATION==================\"\r\n        print \"==============================================\"\r\n        self.ACCOUNT_SID = None\r\n        self.AUTH_TOKEN = None\r\n        self.FROM_NUMBER = None\r\n\r\n        # Get Custom Properties\r\n        try:\r\n            self.ACCOUNT_SID = configurationAttributes.get(\"twilio_sid\").getValue2()\r\n        except:\r\n            print 'TwilioSMS, Missing required configuration attribute \"twilio_sid\"'\r\n\r\n        try:\r\n            self.AUTH_TOKEN = configurationAttributes.get(\"twilio_token\").getValue2()\r\n        except:\r\n            print'TwilioSMS, Missing required configuration attribute \"twilio_token\"'\r\n        try:\r\n            self.FROM_NUMBER = configurationAttributes.get(\"from_number\").getValue2()\r\n        except:\r\n            print'TwilioSMS, Missing required configuration attribute \"from_number\"'\r\n\r\n        if None in (self.ACCOUNT_SID, self.AUTH_TOKEN, self.FROM_NUMBER):\r\n            print \"twilio_sid, twilio_token, from_number is empty ... returning False\"\r\n            return False\r\n\r\n        print \"===TWILIO SMS INITIALIZATION DONE PROPERLY=====\"  \r\n        return True\r\n\r\n    def destroy(self, configurationAttributes):\r\n        print \"Twilio SMS. Destroy\"\r\n        print \"Twilio SMS. Destroyed successfully\"\r\n        return True\r\n\r\n    def getApiVersion(self):\r\n        return 11\r\n        \r\n    def getAuthenticationMethodClaims(self, requestParameters):\r\n        return None\r\n        \r\n    def isValidAuthenticationMethod(self, usageType, configurationAttributes):\r\n        return True\r\n\r\n    def getAlternativeAuthenticationMethod(self, usageType, configurationAttributes):\r\n        return None\r\n\r\n    def authenticate(self, configurationAttributes, requestParameters, step):\r\n        print \"==============================================\"\r\n        print \"====TWILIO SMS AUTHENCATION===================\"\r\n        print \"==============================================\"\r\n        userService = CdiUtil.bean(UserService)\r\n        authenticationService = CdiUtil.bean(AuthenticationService)\r\n        sessionIdService = CdiUtil.bean(SessionIdService)\r\n        facesMessages = CdiUtil.bean(FacesMessages)\r\n        facesMessages.setKeepMessages()\r\n\r\n        session_attributes = self.identity.getSessionId().getSessionAttributes()\r\n        form_passcode = ServerUtil.getFirstValue(requestParameters, \"passcode\")\r\n        form_name = ServerUtil.getFirstValue(requestParameters, \"TwilioSmsloginForm\")\r\n\r\n        print \"TwilioSMS. form_response_passcode: %s\" % str(form_passcode)\r\n\r\n        if step == 1:\r\n            print \"==============================================\"\r\n            print \"=TWILIO SMS STEP 1 | Password Authentication==\"\r\n            print \"==============================================\"\r\n            credentials = self.identity.getCredentials()\r\n            user_name = credentials.getUsername()\r\n            user_password = credentials.getPassword()\r\n            logged_in = False\r\n            if StringHelper.isNotEmptyString(user_name) and StringHelper.isNotEmptyString(user_password):\r\n                logged_in = authenticationService.authenticate(user_name, user_password)\r\n\r\n            if not logged_in:\r\n                return False\r\n\r\n            # Get the Person's number and generate a code\r\n            foundUser = None\r\n            try:\r\n                foundUser = authenticationService.getAuthenticatedUser()\r\n            except:\r\n                print 'TwilioSMS, Error retrieving user %s from LDAP' % (user_name)\r\n                return False\r\n\r\n            try:\r\n                isVerified = foundUser.getAttribute(\"phoneNumberVerified\")\r\n                if isVerified:\r\n                    self.mobile_number = foundUser.getAttribute(\"employeeNumber\")\r\n                if  self.mobile_number == None:\r\n                    self.mobile_number = foundUser.getAttribute(\"mobile\")\r\n                if  self.mobile_number == None:\r\n                    self.mobile_number = foundUser.getAttribute(\"telephoneNumber\")\r\n                if  self.mobile_number == None:\r\n                    print \"TwilioSMS, Error finding mobile number for user '%s'\" % user_name    \r\n                    \r\n            except:\r\n                facesMessages.add(FacesMessage.SEVERITY_ERROR, \"Failed to determine mobile phone number\")\r\n                print 'TwilioSMS, Error finding mobile number for \"%s\". Exception: %s` % (user_name, sys.exc_info()[1])`'\r\n                return False\r\n\r\n            # Generate Random six digit code and store it in array\r\n            code = random.randint(100000, 999999)\r\n\r\n            # Get code and save it in LDAP temporarily with special session entry\r\n            self.identity.setWorkingParameter(\"code\", code)\r\n            sessionId = sessionIdService.getSessionId() # fetch from persistence\r\n            sessionId.getSessionAttributes().put(\"code\", code)\r\n\r\n            try:\r\n                Twilio.init(self.ACCOUNT_SID, self.AUTH_TOKEN);\r\n                message = Message.creator(PhoneNumber(self.mobile_number), PhoneNumber(self.FROM_NUMBER), str(code)).create();\r\n                print \"++++++++++++++++++++++++++++++++++++++++++++++\"\r\n                print 'TwilioSMs, Message Sid: %s' % (message.getSid())\r\n                print 'TwilioSMs, User phone: %s' % (self.mobile_number)\r\n                print \"++++++++++++++++++++++++++++++++++++++++++++++\"\r\n                sessionId.getSessionAttributes().put(\"mobile_number\", self.mobile_number)\r\n                sessionId.getSessionAttributes().put(\"mobile\", self.mobile_number)\r\n                sessionIdService.updateSessionId(sessionId)\r\n                self.identity.setWorkingParameter(\"mobile_number\", self.mobile_number)\r\n                self.identity.getSessionId().getSessionAttributes().put(\"mobile_number\",self.mobile_number)\r\n                self.identity.setWorkingParameter(\"mobile\", self.mobile_number)\r\n                self.identity.getSessionId().getSessionAttributes().put(\"mobile\",self.mobile_number)\r\n                print \"++++++++++++++++++++++++++++++++++++++++++++++\"\r\n                print \"Number: %s\" % (self.identity.getWorkingParameter(\"mobile_number\"))\r\n                print \"Mobile: %s\" % (self.identity.getWorkingParameter(\"mobile\"))\r\n                print \"++++++++++++++++++++++++++++++++++++++++++++++\"\r\n                print \"========================================\"\r\n                print \"===TWILIO SMS FIRST STEP DONE PROPERLY==\"\r\n                print \"========================================\"\r\n                return True\r\n            except Exception, ex:\r\n                facesMessages.add(FacesMessage.SEVERITY_ERROR, \"Failed to send message to mobile phone\")\r\n                print \"TwilioSMS. Error sending message to Twilio\"\r\n                print \"TwilioSMS. Unexpected error:\", ex\r\n\r\n            return False\r\n        elif step == 2:\r\n            # Retrieve the session attribute\r\n            print \"==============================================\"\r\n            print \"=TWILIO SMS STEP 2 | Password Authentication==\"\r\n            print \"==============================================\"\r\n            code = session_attributes.get(\"code\")\r\n            print '=======> Session code is \"%s\"' % str(code)\r\n            sessionIdService = CdiUtil.bean(SessionIdService)\r\n            sessionId = sessionIdService.getSessionId() # fetch from persistence\r\n            code = sessionId.getSessionAttributes().get(\"code\")\r\n            print '=======> Database code is \"%s\"' % str(code)\r\n            self.identity.setSessionId(sessionId)\r\n            print \"==============================================\"\r\n            print \"TwilioSMS. Code: %s\" % str(code)\r\n            print \"==============================================\"\r\n            if code is None:\r\n                print \"TwilioSMS. Failed to find previously sent code\"\r\n                return False\r\n\r\n            if form_passcode is None:\r\n                print \"TwilioSMS. Passcode is empty\"\r\n                return False\r\n\r\n            if len(form_passcode) != 6:\r\n                print \"TwilioSMS. Passcode from response is not 6 digits: %s\" % form_passcode\r\n                return False\r\n\r\n            if form_passcode == code:\r\n                print \"TiwlioSMS, SUCCESS! User entered the same code!\"\r\n                print \"========================================\"\r\n                print \"===TWILIO SMS SECOND STEP DONE PROPERLY\"\r\n                print \"========================================\"\r\n                return True\r\n\r\n            print \"+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++\" \r\n            print \"TwilioSMS. FAIL! User entered the wrong code! %s != %s\" % (form_passcode, code)\r\n            print \"+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++\" \r\n            facesMessages.add(FacesMessage.SEVERITY_ERROR, \"Incorrect Twilio code, please try again.\")\r\n            print \"================================================\"\r\n            print \"===TWILIO SMS SECOND STEP FAILED: INCORRECT CODE\"\r\n            print \"================================================\"\r\n            return False\r\n\r\n        print \"TwilioSMS. ERROR: step param not found or != (1|2)\"\r\n\r\n        return False\r\n\r\n    def prepareForStep(self, configurationAttributes, requestParameters, step):\r\n        if step == 1:\r\n            print \"TwilioSMS. Prepare for Step 1\"\r\n            return True\r\n        elif step == 2:\r\n            print \"TwilioSMS. Prepare for Step 2\"\r\n            return True\r\n        return False\r\n\r\n    def getExtraParametersForStep(self, configurationAttributes, step):\r\n        if step == 2:\r\n            return Arrays.asList(\"code\")\r\n\r\n        return None\r\n\r\n    def getCountAuthenticationSteps(self, configurationAttributes):\r\n        return 2\r\n\r\n    def getPageForStep(self, configurationAttributes, step):\r\n        if step == 2:\r\n            return \"/auth/otp_sms/otp_sms.xhtml\"\r\n\r\n        return \"\"\r\n        \r\n    def getNextStep(self, configurationAttributes, requestParameters, step):\r\n        return -1\r\n\r\n    def getLogoutExternalUrl(self, configurationAttributes, requestParameters):\r\n        print \"Get external logout URL call\"\r\n        return None\r\n        \r\n    def logout(self, configurationAttributes, requestParameters):\r\n        return True\r\n", "person_authentication", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansConfProperty`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("09A0-93D7", "jansCustomScr", "inum=09A0-93D7,ou=scripts,o=jans", "SMPP SMS authentication module", "smpp", '[{"value1": "smpp_server", "value2": "", "description": "IP or FQDN of SMPP server"}, {"value1": "smpp_port", "value2": "", "description": "TCP port of the SMPP server"}, {"value1": "system_id", "value2": "", "description": "Use if SMPP server requires authentication"}, {"value1": "password", "value2": "", "description": "Use if SMPP server requires authentication"}, {"value1": "source_addr_ton", "value2": "", "description": "Type of number, eg ALPHANUMERIC, INTERNATIONAL"}, {"value1": "source_addr", "value2": "", "description": "From number/name"}]', 0, '[{"value1": "usage_type", "value2": "interactive", "description": ""}, {"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2018, Janssen\n# Copyright (c) 2019, Tele2\n\n# Author: Jose Gonzalez\n# Author: Gasmyr Mougang\n# Author: Stefan Andersson\n\nfrom java.util import Arrays, Date\nfrom java.io import IOException\nfrom java.lang import Enum\n\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.as.server.security import Identity\nfrom io.jans.model.custom.script.type.auth import PersonAuthenticationType\nfrom io.jans.as.server.service import AuthenticationService\nfrom io.jans.as.server.service import UserService\nfrom io.jans.as.util import ServerUtil\nfrom io.jans.util import StringHelper, ArrayHelper\nfrom javax.faces.application import FacesMessage\nfrom io.jans.jsf2.message import FacesMessages\n\nfrom org.jsmpp import InvalidResponseException, PDUException\nfrom org.jsmpp.bean import Alphabet, BindType, ESMClass, GeneralDataCoding, MessageClass, NumberingPlanIndicator, RegisteredDelivery, SMSCDeliveryReceipt, TypeOfNumber\nfrom org.jsmpp.extra import NegativeResponseException, ResponseTimeoutException\nfrom org.jsmpp.session import BindParameter, SMPPSession\nfrom org.jsmpp.util import AbsoluteTimeFormatter, TimeFormatter\nimport random\n\n\nclass SmppAttributeError(Exception):\n    pass\n\n\nclass PersonAuthentication(PersonAuthenticationType):\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n        self.identity = CdiUtil.bean(Identity)\n\n    def get_and_parse_smpp_config(self, config, attribute, _type = None,  convert = False, optional = False, default_desc = None):\n        try:\n            value = config.get(attribute).getValue2()\n        except:\n            if default_desc:\n                default_desc = \" using default '{}'\".format(default_desc)\n            else:\n                default_desc = \"\"\n\n            if optional:\n                raise SmppAttributeError(\"SMPP missing optional configuration attribute '{}'{}\".format(attribute, default_desc))\n            else:\n                raise SmppAttributeError(\"SMPP missing required configuration attribute '{}'\".format(attribute))\n\n        if _type and issubclass(_type, Enum):\n            try:\n                return getattr(_type, value)\n            except AttributeError:\n                raise SmppAttributeError(\"SMPP could not find attribute '{}' in {}\".format(attribute, _type))\n\n        if convert:\n            try:\n                value = int(value)\n            except AttributeError:\n                try:\n                    value = int(value, 16)\n                except AttributeError:\n                    raise SmppAttributeError(\"SMPP could not parse value '{}' of attribute '{}'\".format(value, attribute))\n\n        return value\n\n    def init(self, customScript, configurationAttributes):\n        print(\"SMPP Initialization\")\n\n        self.TIME_FORMATTER = AbsoluteTimeFormatter()\n\n        self.SMPP_SERVER = None\n        self.SMPP_PORT = None\n\n        self.SYSTEM_ID = None\n        self.PASSWORD = None\n\n        # Setup some good defaults for TON, NPI and source (from) address\n        # TON (Type of Number), NPI (Number Plan Indicator)\n        self.SRC_ADDR_TON = TypeOfNumber.ALPHANUMERIC    # Alphanumeric\n        self.SRC_ADDR_NPI = NumberingPlanIndicator.ISDN  # ISDN (E163/E164)\n        self.SRC_ADDR = \"Janssen OTP\"\n\n        # Don't touch these unless you know what your doing, we don't handle number reformatting for\n        # any other type than international.\n        self.DST_ADDR_TON = TypeOfNumber.INTERNATIONAL   # International\n        self.DST_ADDR_NPI = NumberingPlanIndicator.ISDN  # ISDN (E163/E164)\n\n        # Priority flag and data_coding bits\n        self.PRIORITY_FLAG = 3  # Very Urgent (ANSI-136), Emergency (IS-95)\n        self.DATA_CODING_ALPHABET = Alphabet.ALPHA_DEFAULT  # SMS default alphabet\n        self.DATA_CODING_MESSAGE_CLASS = MessageClass.CLASS1  # EM (Mobile Equipment (mobile memory), normal message\n\n        # Required server settings\n        try:\n            self.SMPP_SERVER = self.get_and_parse_smpp_config(configurationAttributes, \"smpp_server\")\n        except SmppAttributeError as e:\n            print(e)\n\n        try:\n            self.SMPP_PORT = self.get_and_parse_smpp_config(configurationAttributes, \"smpp_port\", convert = True)\n        except SmppAttributeError as e:\n            print(e)\n\n        if None in (self.SMPP_SERVER, self.SMPP_PORT):\n            print(\"SMPP smpp_server and smpp_port is empty, will not enable SMPP service\")\n            return False\n\n        # Optional system_id and password for bind auth\n        try:\n            self.SYSTEM_ID = self.get_and_parse_smpp_config(configurationAttributes, \"system_id\", optional = True)\n        except SmppAttributeError as e:\n            print(e)\n\n        try:\n            self.PASSWORD = self.get_and_parse_smpp_config(configurationAttributes, \"password\", optional = True)\n        except SmppAttributeError as e:\n            print(e)\n\n        if None in (self.SYSTEM_ID, self.PASSWORD):\n            print(\"SMPP Authentication disabled\")\n\n        # From number and to number settings\n        try:\n            self.SRC_ADDR_TON = self.get_and_parse_smpp_config(\n                configurationAttributes,\n                \"source_addr_ton\",\n                _type = TypeOfNumber,\n                optional = True,\n                default_desc = self.SRC_ADDR_TON\n            )\n        except SmppAttributeError as e:\n            print(e)\n\n        try:\n            self.SRC_ADDR_NPI = self.get_and_parse_smpp_config(\n                configurationAttributes,\n                \"source_addr_npi\",\n                _type = NumberingPlanIndicator,\n                optional = True,\n                default_desc = self.SRC_ADDR_NPI\n            )\n        except SmppAttributeError as e:\n            print(e)\n\n        try:\n            self.SRC_ADDR = self.get_and_parse_smpp_config(\n                configurationAttributes,\n                \"source_addr\",\n                optional = True,\n                default_desc = self.SRC_ADDR\n            )\n        except SmppAttributeError as e:\n            print(e)\n\n        try:\n            self.DST_ADDR_TON = self.get_and_parse_smpp_config(\n                configurationAttributes,\n                \"dest_addr_ton\",\n                _type = TypeOfNumber,\n                optional = True,\n                default_desc = self.DST_ADDR_TON\n            )\n        except SmppAttributeError as e:\n            print(e)\n\n        try:\n            self.DST_ADDR_NPI = self.get_and_parse_smpp_config(\n                configurationAttributes,\n                \"dest_addr_npi\",\n                _type = NumberingPlanIndicator,\n                optional = True,\n                default_desc = self.DST_ADDR_NPI\n            )\n        except SmppAttributeError as e:\n            print(e)\n\n        # Priority flag and data coding, don't touch these unless you know what your doing...\n        try:\n            self.PRIORITY_FLAG = self.get_and_parse_smpp_config(\n                configurationAttributes,\n                \"priority_flag\",\n                convert = True,\n                optional = True,\n                default_desc = \"3 (Very Urgent, Emergency)\"\n            )\n        except SmppAttributeError as e:\n            print(e)\n\n        try:\n            self.DATA_CODING_ALPHABET = self.get_and_parse_smpp_config(\n                configurationAttributes,\n                \"data_coding_alphabet\",\n                _type = Alphabet,\n                optional = True,\n                default_desc = self.DATA_CODING_ALPHABET\n            )\n        except SmppAttributeError as e:\n            print(e)\n\n        try:\n            self.DATA_CODING_MESSAGE_CLASS = self.get_and_parse_smpp_config(\n                configurationAttributes,\n                \"data_coding_alphabet\",\n                _type = MessageClass,\n                optional = True,\n                default_desc = self.DATA_CODING_MESSAGE_CLASS\n            )\n        except SmppAttributeError as e:\n            print(e)\n\n        print(\"SMPP Initialized successfully\")\n        return True\n\n    def destroy(self, configurationAttributes):\n        print(\"SMPP Destroy\")\n        print(\"SMPP Destroyed successfully\")\n        return True\n\n    def getApiVersion(self):\n        return 11\n        \n    def getAuthenticationMethodClaims(self, requestParameters):\n        return None\n        \n    def isValidAuthenticationMethod(self, usageType, configurationAttributes):\n        return True\n\n    def getAlternativeAuthenticationMethod(self, usageType, configurationAttributes):\n        return None\n\n    def authenticate(self, configurationAttributes, requestParameters, step):\n        userService = CdiUtil.bean(UserService)\n        authenticationService = CdiUtil.bean(AuthenticationService)\n\n        facesMessages = CdiUtil.bean(FacesMessages)\n        facesMessages.setKeepMessages()\n\n        session_attributes = self.identity.getSessionId().getSessionAttributes()\n        form_passcode = ServerUtil.getFirstValue(requestParameters, \"passcode\")\n\n        print(\"SMPP form_response_passcode: {}\".format(str(form_passcode)))\n\n        if step == 1:\n            print(\"SMPP Step 1 Password Authentication\")\n            credentials = self.identity.getCredentials()\n\n            user_name = credentials.getUsername()\n            user_password = credentials.getPassword()\n\n            logged_in = False\n            if StringHelper.isNotEmptyString(user_name) and StringHelper.isNotEmptyString(user_password):\n                logged_in = authenticationService.authenticate(user_name, user_password)\n\n            if not logged_in:\n                return False\n\n            # Get the Person's number and generate a code\n            foundUser = None\n            try:\n                foundUser = authenticationService.getAuthenticatedUser()\n            except:\n                print(\"SMPP Error retrieving user {} from LDAP\".format(user_name))\n                return False\n\n            mobile_number = None\n            try:\n                isVerified = foundUser.getAttribute(\"phoneNumberVerified\")\n                if isVerified:\n                    mobile_number = foundUser.getAttribute(\"employeeNumber\")\n                if not mobile_number:\n                    mobile_number = foundUser.getAttribute(\"mobile\")\n                if not mobile_number:\n                    mobile_number = foundUser.getAttribute(\"telephoneNumber\")\n                if not mobile_number:\n                    facesMessages.add(FacesMessage.SEVERITY_ERROR, \"Failed to determine mobile phone number\")\n                    print(\"SMPP Error finding mobile number for user '{}'\".format(user_name))\n                    return False\n            except Exception as e:\n                facesMessages.add(FacesMessage.SEVERITY_ERROR, \"Failed to determine mobile phone number\")\n                print(\"SMPP Error finding mobile number for {}: {}\".format(user_name, e))\n                return False\n\n            # Generate Random six digit code\n            code = random.randint(100000, 999999)\n\n            # Get code and save it in LDAP temporarily with special session entry\n            self.identity.setWorkingParameter(\"code\", code)\n\n            self.identity.setWorkingParameter(\"mobile_number\", mobile_number)\n            self.identity.getSessionId().getSessionAttributes().put(\"mobile_number\", mobile_number)\n            if not self.sendMessage(mobile_number, str(code)):\n                facesMessages.add(FacesMessage.SEVERITY_ERROR, \"Failed to send message to mobile phone\")\n                return False\n\n            return True\n        elif step == 2:\n            # Retrieve the session attribute\n            print(\"SMPP Step 2 SMS/OTP Authentication\")\n            code = session_attributes.get(\"code\")\n            print(\"SMPP Code: {}\".format(str(code)))\n\n            if code is None:\n                print(\"SMPP Failed to find previously sent code\")\n                return False\n\n            if form_passcode is None:\n                print(\"SMPP Passcode is empty\")\n                return False\n\n            if len(form_passcode) != 6:\n                print(\"SMPP Passcode from response is not 6 digits: {}\".format(form_passcode))\n                return False\n\n            if form_passcode == code:\n                print(\"SMPP SUCCESS! User entered the same code!\")\n                return True\n\n            print(\"SMPP failed, user entered the wrong code! {} != {}\".format(form_passcode, code))\n            facesMessages.add(facesMessage.SEVERITY_ERROR, \"Incorrect SMS code, please try again.\")\n            return False\n\n        print(\"SMPP ERROR: step param not found or != (1|2)\")\n        return False\n\n    def prepareForStep(self, configurationAttributes, requestParameters, step):\n        if step == 1:\n            print(\"SMPP Prepare for Step 1\")\n            return True\n        elif step == 2:\n            print(\"SMPP Prepare for Step 2\")\n            return True\n\n        return False\n\n    def getExtraParametersForStep(self, configurationAttributes, step):\n        if step == 2:\n            return Arrays.asList(\"code\")\n\n        return None\n\n    def getCountAuthenticationSteps(self, configurationAttributes):\n        return 2\n\n    def getPageForStep(self, configurationAttributes, step):\n        if step == 2:\n            return \"/auth/otp_sms/otp_sms.xhtml\"\n\n        return \"\"\n\n    def getNextStep(self, configurationAttributes, requestParameters, step):\n        return -1\n\n    def getLogoutExternalUrl(self, configurationAttributes, requestParameters):\n        print \"Get external logout URL call\"\n        return None\n\n    def logout(self, configurationAttributes, requestParameters):\n        return True\n\n    def sendMessage(self, number, code):\n        status = False\n        session = SMPPSession()\n        session.setTransactionTimer(10000)\n\n        # We only handle international destination number reformatting.\n        # All others may vary by configuration decisions taken on SMPP\n        # server side which we have no clue about.\n        if self.DST_ADDR_TON == TypeOfNumber.INTERNATIONAL and number.startswith(\"+\"):\n            number = number[1:]\n\n        try:\n            print(\"SMPP Connecting\")\n            reference_id = session.connectAndBind(\n                self.SMPP_SERVER,\n                self.SMPP_PORT,\n                BindParameter(\n                    BindType.BIND_TX,\n                    self.SYSTEM_ID,\n                    self.PASSWORD,\n                    None,\n                    self.SRC_ADDR_TON,\n                    self.SRC_ADDR_NPI,\n                    None\n                )\n            )\n            print(\"SMPP Connected to server with system id {}\".format(reference_id))\n\n            try:\n                message_id = session.submitShortMessage(\n                    \"CMT\",\n                    self.SRC_ADDR_TON,\n                    self.SRC_ADDR_NPI,\n                    self.SRC_ADDR,\n                    self.DST_ADDR_TON,\n                    self.DST_ADDR_NPI,\n                    number,\n                    ESMClass(),\n                    0,\n                    self.PRIORITY_FLAG,\n                    self.TIME_FORMATTER.format(Date()),\n                    None,\n                    RegisteredDelivery(SMSCDeliveryReceipt.DEFAULT),\n                    0,\n                    GeneralDataCoding(\n                        self.DATA_CODING_ALPHABET,\n                        self.DATA_CODING_MESSAGE_CLASS,\n                        False\n                    ),\n                    0,\n                    code\n                )\n                print(\"SMPP Message '{}' sent to #{} with message id {}\".format(code, number, message_id))\n                status = True\n            except PDUException as e:\n                print(\"SMPP Invalid PDU parameter: {}\".format(e))\n            except ResponseTimeoutException as e:\n                print(\"SMPP Response timeout: {}\".format(e))\n            except InvalidResponseException as e:\n                print(\"SMPP Receive invalid response: {}\".format(e))\n            except NegativeResponseException as e:\n                print(\"SMPP Receive negative response: {}\".format(e))\n            except IOException as e:\n                print(\"SMPP IO error occured: {}\".format(e))\n            finally:\n                session.unbindAndClose()\n        except IOException as e:\n            print(\"SMPP Failed connect and bind to host: {}\".format(e))\n\n        return status\n", "person_authentication", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("DAA9-B789", "jansCustomScr", "inum=DAA9-B789,ou=scripts,o=jans", "Sample Application Session script", "application_session", 0, '[{"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2016, Janssen\n#\n# Author: Yuriy Movchan\n#\n\nfrom io.jans.model.custom.script.type.session import ApplicationSessionType\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.persist import PersistenceEntryManager\nfrom io.jans.as.model.config import StaticConfiguration\nfrom io.jans.as.model.ldap import TokenLdap\nfrom javax.faces.application import FacesMessage\nfrom io.jans.jsf2.message import FacesMessages\nfrom io.jans.util import StringHelper, ArrayHelper\nfrom io.jans.as.model.config import Constants\nfrom java.util import Arrays, ArrayList\nfrom io.jans.as.service.external.session import SessionEventType\n\nimport java\n\nclass ApplicationSession(ApplicationSessionType):\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript, configurationAttributes):\n        print \"Application session. Initialization\"\n\n        self.entryManager = CdiUtil.bean(PersistenceEntryManager)\n        self.staticConfiguration = CdiUtil.bean(StaticConfiguration)\n\n        print \"Application session. Initialized successfully\"\n\n        return True   \n\n    def destroy(self, configurationAttributes):\n        print \"Application session. Destroy\"\n        print \"Application session. Destroyed successfully\"\n        return True   \n\n    def getApiVersion(self):\n        return 11\n\n    # Called each time specific session event occurs\n    # event is io.jans.as.service.external.session.SessionEvent\n    def onEvent(self, event):\n        if event.getType() == SessionEventType.AUTHENTICATED:\n            print \"Session is authenticated, session: \" + event.getSessionId().getId()\n        return\n\n    # Application calls it at start session request to allow notify 3rd part systems\n    #   httpRequest is javax.servlet.http.HttpServletRequest\n    #   sessionId is io.jans.as.model.common.SessionId\n    #   configurationAttributes is java.util.Map<String, SimpleCustomProperty>\n    def startSession(self, httpRequest, sessionId, configurationAttributes):\n        print \"Application session. Starting external session\"\n\n        user_name = sessionId.getSessionAttributes().get(Constants.AUTHENTICATED_USER)\n\n        first_session = self.isFirstSession(user_name)\n        if not first_session:\n            facesMessages = CdiUtil.bean(FacesMessages)\n            facesMessages.add(FacesMessage.SEVERITY_ERROR, \"Please, end active session first!\")\n            return False\n\n        print \"Application session. External session started successfully\"\n        return True\n\n    # Application calls it at end session request to allow notify 3rd part systems\n    #   httpRequest is javax.servlet.http.HttpServletRequest\n    #   sessionId is io.jans.as.model.common.SessionId\n    #   configurationAttributes is java.util.Map<String, SimpleCustomProperty>\n    def endSession(self, httpRequest, sessionId, configurationAttributes):\n        print \"Application session. Starting external session end\"\n\n        print \"Application session. External session ended successfully\"\n        return True\n\n    def isFirstSession(self, user_name):\n        tokenLdap = TokenLdap()\n        tokenLdap.setDn(self.staticConfiguration.getBaseDn().getClients())\n        tokenLdap.setUserId(user_name)\n\n        tokenLdapList = self.entryManager.findEntries(tokenLdap, 1)\n        print \"Application session. isFirstSession. Get result: '%s'\" % tokenLdapList\n\n        if (tokenLdapList != None) and (tokenLdapList.size() > 0):\n            print \"Application session. isFirstSession: False\"\n            return False\n\n        print \"Application session. isFirstSession: True\"\n        return True\n", "application_session", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("DAA9-BA60", "jansCustomScr", "inum=DAA9-BA60,ou=scripts,o=jans", "Consent Gathering script", "consent_gathering", 0, '[{"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2017, Janssen\n#\n# Author: Yuriy Movchan\n#\n\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.as.server.security import Identity\nfrom io.jans.model.custom.script.type.authz import ConsentGatheringType\nfrom io.jans.util import StringHelper\n\nimport java\nimport random\n\nclass ConsentGathering(ConsentGatheringType):\n\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript, configurationAttributes):\n        print \"Consent-Gathering. Initializing ...\"\n        print \"Consent-Gathering. Initialized successfully\"\n\n        return True\n\n    def destroy(self, configurationAttributes):\n        print \"Consent-Gathering. Destroying ...\"\n        print \"Consent-Gathering. Destroyed successfully\"\n\n        return True\n\n    def getApiVersion(self):\n        return 1\n\n    # Main consent-gather method. Must return True (if gathering performed successfully) or False (if fail).\n    # All user entered values can be access via Map<String, String> context.getPageAttributes()\n    def authorize(self, step, context): # context is reference of io.jans.as.service.external.context.ConsentGatheringContext\n        print \"Consent-Gathering. Authorizing...\"\n\n        if step == 1:\n            allowButton = context.getRequestParameters().get(\"authorizeForm:allowButton\")\n            if (allowButton != None) and (len(allowButton) > 0):\n                print \"Consent-Gathering. Authorization success for step 1\"\n                return True\n\n            print \"Consent-Gathering. Authorization declined for step 1\"\n        elif step == 2:\n            allowButton = context.getRequestParameters().get(\"authorizeForm:allowButton\")\n            if (allowButton != None) and (len(allowButton) > 0):\n                print \"Consent-Gathering. Authorization success for step 2\"\n                return True\n\n            print \"Consent-Gathering. Authorization declined for step 2\"\n\n        return False\n\n    def getNextStep(self, step, context):\n        return -1\n\n    def prepareForStep(self, step, context):\n        if not context.isAuthenticated():\n            print \"User is not authenticated. Aborting authorization flow ...\"\n            return False\n\n        if step == 2:\n            pageAttributes = context.getPageAttributes()\n            \n            # Generate random consent gathering request\n            consentRequest = \"Requested transaction #%s approval for the amount of sum $ %s.00\" % ( random.randint(100000, 1000000), random.randint(1, 100) )\n            pageAttributes.put(\"consent_request\", consentRequest)\n            return True\n\n        return True\n\n    def getStepsCount(self, context):\n        return 11\n\n    def getPageForStep(self, step, context):\n        if step == 1:\n            return \"/authz/authorize.xhtml\"\n        elif step == 2:\n            return \"/authz/transaction.xhtml\"\n\n        return \"\"\n", "consent_gathering", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `displayName`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("8AF7.D82A", "jansCustomScr", "inum=8AF7.D82A,ou=scripts,o=jans", "persistence_extension", 0, '[{"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2020, Janssen\n#\n# Author: Yuriy Movchan\n#\n\nfrom io.jans.service.cdi.util import CdiUtil\nfrom io.jans.model.custom.script.type.persistence import PersistenceType\nfrom io.jans.util import StringHelper\nfrom io.jans.persist.operation.auth import PasswordEncryptionHelper\nfrom io.jans.persist.operation.auth import PasswordEncryptionMethod\n\nimport java\n\nclass PersistenceExtension(PersistenceType):\n\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript, configurationAttributes):\n        print \"Persistence extension. Initialization\"\n        return True\n\n    def destroy(self, configurationAttributes):\n        print \"Persistence extension. Destroy\"\n        return True\n\n    def getApiVersion(self):\n        return 11\n\n    def onAfterCreate(self, context, configurationAttributes):\n        print \"Persistence extension. Method: onAfterCreate\"\n\n    def onAfterDestroy(self, context, configurationAttributes):\n        print \"Persistence extension. Method: onAfterDestroy\"\n\n    def createHashedPassword(self, credential):\n        print \"Persistence extension. Method: createHashedPassword\"\n\n        hashed_password= PasswordEncryptionHelper.createStoragePassword(credential, PasswordEncryptionMethod.HASH_METHOD_PKCS5S2)\n\n        return hashed_password\n\n    def compareHashedPasswords(self, credential, storedCredential):\n        print \"Persistence extension. Method: compareHashedPasswords\"\n        \n        auth_result = PasswordEncryptionHelper.compareCredentials(credential, storedCredential)\n\n        return auth_result \n", "persistence_extension", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `displayName`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("8AF7.D82B", "jansCustomScr", "inum=8AF7.D82B,ou=scripts,o=jans", "idp", 0, '[{"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxShibboleth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2020, Janssen\n#\n# Author: Yuriy Movchan\n#\n\nfrom io.jans.model.custom.script.type.idp import IdpType\nfrom io.jans.util import StringHelper\nfrom io.jans.idp.externalauth import AuthenticatedNameTranslator\nfrom net.shibboleth.idp.authn.principal import UsernamePrincipal, IdPAttributePrincipal\nfrom net.shibboleth.idp.authn import ExternalAuthentication\nfrom net.shibboleth.idp.attribute import IdPAttribute, StringAttributeValue\nfrom net.shibboleth.idp.authn.context import AuthenticationContext, ExternalAuthenticationContext\nfrom net.shibboleth.idp.attribute.context import AttributeContext\nfrom javax.security.auth import Subject\nfrom java.util import Collections, HashMap, HashSet, ArrayList, Arrays\n\nimport java\n\nclass IdpExtension(IdpType):\n\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, customScript, configurationAttributes):\n        print \"Idp extension. Initialization\"\n        \n        self.defaultNameTranslator = AuthenticatedNameTranslator()\n        \n        return True\n\n    def destroy(self, configurationAttributes):\n        print \"Idp extension. Destroy\"\n        return True\n\n    def getApiVersion(self):\n        return 11\n\n    # Translate attributes from user profile\n    #   context is io.jans.idp.externalauth.TranslateAttributesContext (https://github.com/JanssenFederation/shib-oxauth-authn3/blob/master/src/main/java/io.jans.idp/externalauth/TranslateAttributesContext.java)\n    #   configurationAttributes is java.util.Map<String, SimpleCustomProperty>\n    def translateAttributes(self, context, configurationAttributes):\n        print \"Idp extension. Method: translateAttributes\"\n        \n        # Return False to use default method\n        #return False\n        \n        request = context.getRequest()\n        userProfile = context.getUserProfile()\n        principalAttributes = self.defaultNameTranslator.produceIdpAttributePrincipal(userProfile.getAttributes())\n        print \"Idp extension. Converted user profile: '%s' to attribute principal: '%s'\" % (userProfile, principalAttributes)\n\n        if not principalAttributes.isEmpty():\n            print \"Idp extension. Found attributes from oxAuth. Processing...\"\n            \n            # Start: Custom part\n            # Add givenName attribute\n            givenNameAttribute = IdPAttribute(\"oxEnrollmentCode\")\n            givenNameAttribute.setValues(ArrayList(Arrays.asList(StringAttributeValue(\"Dummy\"))))\n            principalAttributes.add(IdPAttributePrincipal(givenNameAttribute))\n            print \"Idp extension. Updated attribute principal: '%s'\" % principalAttributes\n            # End: Custom part\n\n            principals = HashSet()\n            principals.addAll(principalAttributes)\n            principals.add(UsernamePrincipal(userProfile.getId()))\n\n            request.setAttribute(ExternalAuthentication.SUBJECT_KEY, Subject(False, Collections.singleton(principals),\n                Collections.emptySet(), Collections.emptySet()))\n\n            print \"Created an IdP subject instance with principals containing attributes for: '%s'\" % userProfile.getId()\n\n            if False:\n                idpAttributes = ArrayList()\n                for principalAttribute in principalAttributes:\n                    idpAttributes.add(principalAttribute.getAttribute())\n    \n                request.setAttribute(ExternalAuthentication.ATTRIBUTES_KEY, idpAttributes)\n    \n                authenticationKey = context.getAuthenticationKey()\n                profileRequestContext = ExternalAuthentication.getProfileRequestContext(authenticationKey, request)\n                authContext = profileRequestContext.getSubcontext(AuthenticationContext)\n                extContext = authContext.getSubcontext(ExternalAuthenticationContext)\n    \n                extContext.setSubject(Subject(False, Collections.singleton(principals), Collections.emptySet(), Collections.emptySet()));\n    \n                extContext.getSubcontext(AttributeContext, True).setUnfilteredIdPAttributes(idpAttributes)\n                extContext.getSubcontext(AttributeContext).setIdPAttributes(idpAttributes)\n        else:\n            print \"No attributes released from oxAuth. Creating an IdP principal for: '%s'\" % userProfile.getId()\n            request.setAttribute(ExternalAuthentication.PRINCIPAL_NAME_KEY, userProfile.getId())\n\n        #Return True to specify that default method is not needed\n        return False\n\n    # Update attributes before releasing them\n    #   context is io.jans.idp.consent.processor.PostProcessAttributesContext (https://github.com/JanssenFederation/shib-oxauth-authn3/blob/master/src/main/java/io.jans.idp/consent/processor/PostProcessAttributesContext.java)\n    #   configurationAttributes is java.util.Map<String, SimpleCustomProperty>\n    def updateAttributes(self, context, configurationAttributes):\n        print \"Idp extension. Method: updateAttributes\"\n        attributeContext = context.getAttributeContext()\n\n        customAttributes = HashMap()\n        customAttributes.putAll(attributeContext.getIdPAttributes())\n\n        # Remove givenName attribute\n        customAttributes.remove(\"givenName\")\n\n        # Update surname attribute\n        if customAttributes.containsKey(\"sn\"):\n            customAttributes.get(\"sn\").setValues(ArrayList(Arrays.asList(StringAttributeValue(\"Dummy\"))))\n        \n        # Set updated attributes\n        attributeContext.setIdPAttributes(customAttributes.values())\n\n        return True\n", "idp", 0, "python");
INSERT INTO jansCustomScr (`doc_id`, `objectClass`, `dn`, `description`, `displayName`, `jansLevel`, `jansModuleProperty`, `jansRevision`, `jansScr`, `jansScrTyp`, `jansEnabled`, `jansProgLng`) VALUES ("C1BA-C1BA", "jansCustomScr", "inum=C1BA-C1BA,ou=scripts,o=jans", "Firebase notification sender", "firebase_ciba_end_user_notification", 0, '[{"value1": "location_type", "value2": "ldap", "description": ""}]', 1, "# oxAuth is available under the MIT License (2008). See http://opensource.org/licenses/MIT for full text.\n# Copyright (c) 2018, Janssen\n#\n# Author: Milton BO\n#\n#\n\nfrom io.jans.as.client.fcm import FirebaseCloudMessagingResponse\nfrom io.jans.as.client.fcm import FirebaseCloudMessagingClient\nfrom io.jans.as.client.fcm import FirebaseCloudMessagingRequest\nfrom io.jans.as.util import RedirectUri\nfrom io.jans.model.custom.script.type.ciba import EndUserNotificationType\nfrom java.lang import String\nfrom java.util import UUID\n\nclass EndUserNotification(EndUserNotificationType):\n    def __init__(self, currentTimeMillis):\n        self.currentTimeMillis = currentTimeMillis\n\n    def init(self, configurationAttributes):\n        print \"Firebase EndUserNotification script. Initializing ...\"\n        print \"Firebase EndUserNotification script. Initialized successfully\"\n\n        return True\n\n    def destroy(self, configurationAttributes):\n        print \"Firebase EndUserNotification script. Destroying ...\"\n        print \"Firebase EndUserNotification script. Destroyed successfully\"\n        return True\n\n    def getApiVersion(self):\n        return 1\n\n    # Returns boolean true or false depending on the process, if the notification\n    # is sent successfully or not.\n    def notifyEndUser(self, context):\n        print 'Sending push notification using Firebase Cloud Messaging'\n        appConfiguration = context.getAppConfiguration()\n        encryptionService = context.getEncryptionService()\n        clientId = appConfiguration.getBackchannelClientId()\n        redirectUri = appConfiguration.getBackchannelRedirectUri()\n        url = appConfiguration.getCibaEndUserNotificationConfig().getNotificationUrl()\n        key = encryptionService.decrypt(appConfiguration.getCibaEndUserNotificationConfig().getNotificationKey(), True)\n        to = context.getDeviceRegistrationToken()\n        title = \"oxAuth Authentication Request\"\n        body = \"Client Initiated Backchannel Authentication (CIBA)\"\n\n        authorizationRequestUri = RedirectUri(appConfiguration.getAuthorizationEndpoint())\n        authorizationRequestUri.addResponseParameter(\"client_id\", clientId)\n        authorizationRequestUri.addResponseParameter(\"response_type\", \"id_token\")\n        authorizationRequestUri.addResponseParameter(\"scope\", context.getScope())\n        authorizationRequestUri.addResponseParameter(\"acr_values\", context.getAcrValues())\n        authorizationRequestUri.addResponseParameter(\"redirect_uri\", redirectUri)\n        authorizationRequestUri.addResponseParameter(\"state\", UUID.randomUUID().toString())\n        authorizationRequestUri.addResponseParameter(\"nonce\", UUID.randomUUID().toString())\n        authorizationRequestUri.addResponseParameter(\"prompt\", \"consent\")\n        authorizationRequestUri.addResponseParameter(\"auth_req_id\", context.getAuthReqId())\n\n        clickAction = authorizationRequestUri.toString()\n\n        firebaseCloudMessagingRequest = FirebaseCloudMessagingRequest(key, to, title, body, clickAction)\n        firebaseCloudMessagingClient = FirebaseCloudMessagingClient(url)\n        firebaseCloudMessagingClient.setRequest(firebaseCloudMessagingRequest)\n        firebaseCloudMessagingResponse = firebaseCloudMessagingClient.exec()\n\n        responseStatus = firebaseCloudMessagingResponse.getStatus()\n        print \"CIBA: firebase cloud messaging result status \" + str(responseStatus)\n        return (responseStatus >= 200 and responseStatus < 300 )\n\n", "ciba_end_user_notification", 0, "python");
